INSERT INTO `danh_lam_thang_canh` (`pk_id`, `hot`, `ten`, `noi_dung`, `chi_tiet`, `anh`) VALUES
(2, 0, 'Hạ Long đẹp lung linh với màu sắc đen trắng trên báo nước ngoài', '<p>du lich ha long</p>\r\n', '<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#2980b9\"><span style=\"font-size:16px\"><strong>Hạ Long đẹp lung linh với m&agrave;u sắc đen trắng tr&ecirc;n b&aacute;o nước ngo&agrave;i</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>7 trải nghiệm th&uacute; vị tr&ecirc;n Vịnh Hạ Long</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Đ&atilde; bao l&acirc;u rồi bạn chưa đi du lịch Hạ Long? B&acirc;y giờ đ&atilde; c&oacute; rất nhiều trải nghiệm mới v&ocirc; c&ugrave;ng th&uacute; vị d&agrave;nh cho bạn đấy!</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Ch&egrave;o thuyền Kayak</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-1.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Sẽ l&agrave; một điều kh&aacute; đ&aacute;ng tiếc nếu bạn đu du lịch Hạ Long m&agrave; kh&ocirc;ng tham gia c&aacute;c trải nghiệm kh&aacute;m ph&aacute; Vinh bằng thuyền kayak. Ngồi tr&ecirc;n chiếc thuyền b&eacute; nhỏ, khua m&aacute;i ch&egrave;o tr&ecirc;n l&agrave;n nước trong xanh, len lỏi&nbsp;v&agrave;o c&aacute;c v&aacute;ch n&uacute;i, ngắm khung cảnh biển trời đẹp h&uacute;t hồn, vừa thật giản dị nhưng lại v&ocirc; c&ugrave;ng th&uacute; vị.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-2.png\" style=\"float:left; height:150px; width:200px\" type=\"image\" /></span></span><span style=\"color:#000000\"><span style=\"font-size:14px\">Vịnh Hạ long l&agrave; 1 trong 25 điểm du lịch ch&egrave;o thuyền Kayak th&uacute; vị nhất </span></span><span style=\"color:#000000\"><span style=\"font-size:14px\">tr&ecirc;n thế giới do tạp ch&iacute; National Geographic Adventures b&igrave;nh chọn. Ở&nbsp;Vịnh Hạ Long c&oacute; rất nhiều nơi cho thu&ecirc; thuyền Kayak, gi&aacute; thu&ecirc; từ 100200k/h t&ugrave;y từng loại thuyền</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Ngủ đ&ecirc;m tr&ecirc;n du thuyền</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-3.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-4.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Du thuyền tr&ecirc;n vịnh Hạ Long như l&agrave; một kh&aacute;ch sạn thu nhỏ với mỗi cabin l&agrave; 1 ph&ograve;ng ri&ecirc;ng c&oacute; đầy đủ chăn đệm sạch sẽ, điều h&ograve;a, ph&ograve;ng tắm ri&ecirc;ng,&hellip; Bạn vừa c&oacute; thể thư gi&atilde;n trong ph&ograve;ng ri&ecirc;ng, l&ecirc;nh đ&ecirc;nh tr&ecirc;n biển ngắm nước non bao la h&ugrave;ng vĩ qua khung cửa sổ, sẽ l&agrave; một cảm gi&aacute;c v&ocirc; c&ugrave;ng th&uacute; vị. Bạn cũng c&oacute; thể l&ecirc;n boong t&agrave;u ngắm sao trời, ngắm cảnh biển về đ&ecirc;m, tham gia c&aacute;c tr&ograve; chơi vui nhộn, c&acirc;u mực đ&ecirc;m v&ocirc; c&ugrave;ng hấp dẫn. .</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Ngắm Vịnh Hạ Long từ thủy phi cơ</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-5.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Nếu việc ngắm nh&igrave;n khung cảnh Vịnh Hạ Long từ du thuyền vẫn chưa l&agrave;m bạn thấy m&atilde;n nh&atilde;n bởi vẫn c&ograve;n nhiều g&oacute;c nh&igrave;n đẹp ở hạ Long m&agrave; đi thuyền kh&ocirc;ng thể thấy được th&igrave; bạn h&atilde;y thử trải nghiệm ngắm nh&igrave;n to&agrave;n cảnh Vịnh Hạ Long từ thủy phi cơ.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Hiện tại, h&atilde;ng h&agrave;ng kh&ocirc;ng Hải &Acirc;u cung cấp dịch vũ thủy phi cơ với những dịch vụ bay tr&ecirc;n vịnh ngắm cảnh v&ocirc; c&ugrave;ng cao cấp. Một m&aacute;y bay c&oacute; tới 12 chỗ ngồi cho h&agrave;nh kh&aacute;ch với cửa k&iacute;nh th&oacute;ng rộng v&ocirc; c&ugrave;ng l&yacute; tưởng cho du kh&aacute;ch thưởng ngoạn phong cảnh Hạ Long v&agrave; ghi lại những h&igrave;nh ảnh tuyệt đẹp của vịnh từ độ cao 150 &ndash; 3000m so với mực nước biển.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Năm 2015, tạp ch&iacute; The New York Times (Mỹ) cũng đ&atilde; b&igrave;nh chọn dịch vụ ngắm Vịnh Hạ Long từ thủy phi cơ l&agrave; một trong những dịch vụ hấp dẫn nhất.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Thử sức với bộ m&ocirc;n leo n&uacute;i</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-8.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">B&ecirc;n cạnh vẻ đẹp non nước hữu t&igrave;nh thơ mộng, Vịnh Hạ Long với nhiều khối n&uacute;i đa dạng, địa h&igrave;nh hiểm trở cũng c&oacute; một sức h&uacute;t v&ocirc; c&ugrave;ng lớn với c&aacute;ch kh&aacute;ch du lịch y&ecirc;u th&iacute;ch thể thao mạo hiểm. Một t&iacute;n đồ của m&ocirc;n thể thao mạo hiểm n&agrave;y chắc chắn sẽ kh&ocirc;ng thể bỏ qua những g&oacute;c n&uacute;i non uốn lượn, những v&aacute;ch đ&aacute;, hang động đẹp tuyệt tại Vịnh Hạ Long.&nbsp;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Kh&aacute;m ph&aacute; những hang động tuyệt đẹp</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-9.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Vịnh Hạ Long được v&iacute; như bức tranh thủy thi&ecirc;n nhi&ecirc;n đẹp đến ho&agrave;n hảo. V&agrave; tr&ecirc;n bức tranh thủy mặc đ&oacute;, b&ecirc;n cạnh biển v&agrave; n&uacute;i đ&aacute;, th&igrave; nơi đ&acirc;y c&ograve;n nổi tiếng với nhữnghang động độc đ&aacute;o đẹp tuyệt. Mỗi hang động ở Hạ Long đều c&oacute; mộtc &aacute;i t&ecirc;n th&uacute; vị, khơi gợi sự t&ograve; m&ograve;, ham muốn kh&aacute;m ph&aacute; của du kh&aacute;ch như: Động Thi&ecirc;n Cung, hang Luồn, hang Sửng Sốt, hang Đầu Gỗ, &hellip;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Thăm c&aacute;c l&agrave;ng ch&agrave;i tr&ecirc;n Vịnh</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-11.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Rời xa &aacute;nh đ&egrave;n xa hoa cũng như kh&oacute;i bụi của th&agrave;nh phố, đi xa khỏi những x&ocirc; bồ mệt mỏi nơi th&agrave;nh thị, c&aacute;c l&agrave;ng ch&agrave;i tr&ecirc;n Vịnh H&agrave; Long thật trong l&agrave;nh, b&igrave;nh y&ecirc;n v&agrave; cũng l&agrave; một điểm đến th&uacute; vị cho c&aacute;c du kh&aacute;ch. Một số l&agrave;ng ch&agrave;i nổi bật m&agrave; bạn n&ecirc;n gh&eacute; thăm như , l&agrave;ng ch&agrave;i Ba Hang, l&agrave;ng ch&agrave;i Cửa Vạn, tại l&agrave;ng ch&agrave;i Vung Vi&ecirc;ng,&hellip; Để đến đ&acirc;y bạn c&oacute; thể thu&ecirc; thuyền tại c&aacute;c bến t&agrave;u du lịch.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>&nbsp;</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Đi C&aacute;p treo 2 tầng ngắm cảnh to&agrave;n Vịnh</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong><input alt=\"\" src=\"http://giacmoviet.vn/UserFiles/image/cam%20nang%20viet%20nam/ha%20long/trai-nghiem-vinh-ha-long-13.jpg\" style=\"float:right; height:150px; width:200px\" type=\"image\" /></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Ng&agrave;y 25/6/2016, sau 2 năm x&acirc;y dựng, hệ thống c&aacute;p treo Hạ Long đ&atilde; được kh&aacute;nh th&agrave;nh v&agrave; ch&iacute;nh thức đi v&agrave;o hoạt động. C&ocirc;ng tr&igrave;nh n&agrave;y thuộc tổ &aacute;n khu vui chơi giải tr&iacute; trải dọc bờ biển B&atilde;i Ch&aacute;y tới đồi B&agrave; Đ&egrave;o (H&ograve;n Gai) c&oacute; trị gi&aacute; l&ecirc;n tới 6 000 tỷ đồng</span></span></p>\r\n', '1521812570HaLongDepLungLinh.jpg'),
(3, 1, 'Chiêm ngưỡng sông Mekong và những dòng sông đẹp nhất thế giới', '<p>S&ocirc;ng Mekong kh&ocirc;ng chỉ c&oacute; cảnh quan ngoạn mục, 14 d&ograve;ng s&ocirc;ng vừa được b&aacute;o Anh Telegraph giới thiệu trong b&agrave;i viết</p>\r\n', '<h1 style=\"text-align:center\"><span style=\"color:#2980b9\"><span style=\"font-size:16px\"><strong>Chi&ecirc;m ngưỡng s&ocirc;ng Mekong v&agrave; những d&ograve;ng s&ocirc;ng đẹp nhất thế giới</strong></span></span></h1>\r\n\r\n<h1><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">1.&nbsp;S&ocirc;ng Kenai, Alaska, Mỹ</span></a></span></strong></h1>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/1M4WqicT.jpg\" style=\"float:right; height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">S&ocirc;ng Kenai c&oacute;&nbsp;chiều d&agrave;i khoảng 132km, chạy dọc theo hướng t&acirc;y, từ d&atilde;y n&uacute;i Kenai qua Kenai National Wildlife Refuge v&agrave; hồ Skilak trước khi đổ ra biển Th&aacute;i B&igrave;nh Dương.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Việc đ&aacute;nh bắt c&aacute; hồi ở Kenai l&agrave; một trong những điểm nhấn ch&iacute;nh thu h&uacute;t du kh&aacute;ch, trong đ&oacute; th&aacute;ng 7 v&agrave; 8 l&agrave; m&ugrave;a cao điểm của việc đ&aacute;nh bắt.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Cảnh quan thi&ecirc;n nhi&ecirc;n tuyệt đẹp, nhiều trải nghiệm du lịch tuyệt vời như c&aacute;c hoạt động c&acirc;u c&aacute; thể thao, ch&egrave;o thuyền kayak&hellip; sẽ l&agrave; điểm đến trong mơ cho chuyến du lịch của bạn.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">2.&nbsp;S&ocirc;ng Irrawaddy, Burma, Myanmar</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/Rg1TXX8S.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">L&agrave; con s&ocirc;ng d&agrave;i nhất v&agrave; trở th&agrave;nh một trong những điểm tham quan ấn tượng nhất nước n&agrave;y.&nbsp;</span></span><span style=\"color:null\"><span style=\"font-size:14px\">Những bờ s&ocirc;ng với&nbsp;rừng rậm phủ d&agrave;y đặc, những hẻm n&uacute;i lớn, c&aacute;c c&aacute;nh đồng l&uacute;a bất tận, những khoảnh khắc đời thường của cuộc sống n&ocirc;ng th&ocirc;n, những h&igrave;nh ảnh dung dị của c&aacute;c nh&agrave; sư&hellip; khiến khung cảnh dọc con s&ocirc;ng Irrawaddy rất đẹp v&agrave; thơ mộng.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Trung-Quoc-26.html\"><span style=\"color:#c0392b\">3.&nbsp;S&ocirc;ng Dương Tử, Trung Quốc</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/5CkG40nl.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Được cung cấp nước từ những tảng băng ở Qinghai-Tibet Plateau,&nbsp;Dương Tử l&agrave; con s&ocirc;ng d&agrave;i nhất&nbsp;ch&acirc;u &Aacute;, chảy qua 10 tỉnh ph&iacute;a đ&ocirc;ng,&nbsp;sau đ&oacute; đổ ra biển ở ph&iacute;a bắc TP&nbsp;Thượng Hải. Kh&ocirc;ng chỉ c&oacute; tiềm năng to lớn về điện năng, đ&acirc;y c&ograve;n l&agrave; nơi&nbsp;cung cấp nước&nbsp;tưới ti&ecirc;u cho c&aacute;c c&aacute;nh đồng ruộng v&agrave; nguồn thực phẩm phong ph&uacute; cho người d&acirc;n nơi đ&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Một trong những điểm tham quan nổi bật l&agrave; nh&agrave; m&aacute;y điện lớn nhất thế giới Three Gorges Dam. Ngo&agrave;i ra&nbsp;bạn cũng c&oacute; thể chu du tr&ecirc;n c&aacute;c t&agrave;u thuyền để&nbsp;kh&aacute;m ph&aacute; v&agrave; chi&ecirc;m ngưỡng cảnh đẹp th&ocirc;n qu&ecirc;.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Chau-Au-30.html\"><span style=\"color:#c0392b\">4.&nbsp;S&ocirc;ng Rhine, ch&acirc;u &Acirc;u</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/Ym1siaSB.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:null\">C&oacute; chiều d&agrave;i 1.320km, chảy qua 6 nước</span><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Chau-Au-30.html\"><span style=\"color:null\">&nbsp;&Aacute;o, Ph&aacute;p, Đức, Liechtenstein, H&agrave; Lan v&agrave; Thụy Sĩ</span></a><span style=\"color:null\">&nbsp;v&agrave; l&agrave; con s&ocirc;ng nhộn nhịp v&agrave; c&oacute; đường thủy đẹp nhất ch&acirc;u &Acirc;u.&nbsp;</span></span><span style=\"color:null\"><span style=\"font-size:14px\">Những l&acirc;u đ&agrave;i, chứng t&iacute;ch thời trung cổ, những c&aacute;nh đồng nho thoai thoải tr&ecirc;n v&ugrave;ng Lorelei Rock, c&aacute;c nh&agrave; thờ v&agrave; đồ cổ từ thời La M&atilde;, những &aacute;nh đ&egrave;n lung linh ở cung điện tr&ecirc;n đỉnh đồi&hellip; tạo cho d&ograve;ng s&ocirc;ng một quan cảnh vừa thơ mộng vừa huyền ảo, khiến bất cứ du kh&aacute;ch n&agrave;o đến đ&acirc;y cũng&nbsp;phải ngỡ ng&agrave;ng v&agrave; ngợi ca.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">5.&nbsp;S&ocirc;ng Cano Cristales, Columbia</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/G3LuqikF.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Được ca ngợi l&agrave; &ldquo;d&ograve;ng s&ocirc;ng ngũ sắc&rdquo; hoặc &ldquo;cầu v&ograve;ng lỏng&rdquo;, s&ocirc;ng Cano Cristales nằm trong một khu vực xa x&ocirc;i thuộc tỉnh Meta, Columbia. Tuy địa h&igrave;nh kh&oacute; tiếp cận, nhưng vẻ đẹp ngoạn mục của m&agrave;u sắc trải d&agrave;i suốt chiều d&agrave;i con s&ocirc;ng&nbsp;đ&atilde; quyến rũ nhiều&nbsp;lữ h&agrave;nh đến đ&acirc;y chinh phục.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Du kh&aacute;ch c&oacute; thể tham quan v&agrave;o th&aacute;ng 7&nbsp;đến th&aacute;ng 11, v&igrave; thời điểm n&agrave;y l&ograve;ng s&ocirc;ng c&oacute; m&agrave;u sắc sặc sỡ của m&agrave;u v&agrave;ng, xanh l&aacute; c&acirc;y, xanh dương, đen v&agrave; đỏ&nbsp;do nồng độ cao của tảo,&nbsp;khiến khung cảnh c&agrave;ng th&ecirc;m&nbsp;đẹp v&agrave; rực rỡ.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">6.&nbsp;S&ocirc;ng Mekong, Đ&ocirc;ng Nam &Aacute;</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/psJUoeAZ.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">S&ocirc;ng Mekong bắt đầu từ cao nguy&ecirc;n T&acirc;y Tạng, chảy qua tỉnh V&acirc;n Nam (Trung Quốc), Myanmar, L&agrave;o, Th&aacute;i Lan, Campuchia v&agrave; Việt Nam trước khi đổ ra biển&nbsp;Đ&ocirc;ng. C&aacute;c tuyến tham quan du lịch bằng đường s&ocirc;ng giữa c&aacute;c nước như đến Siem Reap (Campuchia) kh&aacute;m ph&aacute; di t&iacute;ch Angkor Wat hay gh&eacute; th&ocirc; thị sầm uất TP.HCM (Việt Nam) lu&ocirc;n thu h&uacute;t du kh&aacute;ch.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Vi vu miền s&ocirc;ng nước Mekong bạn&nbsp;sẽ bị cuốn h&uacute;t bởi vẻ đẹp của cuộc sống cư d&acirc;n nơi đ&acirc;y với&nbsp;c&aacute;c l&agrave;ng b&egrave;,&nbsp;chợ nổi, c&aacute;nh đồng l&uacute;a, những ng&ocirc;i ch&ugrave;a&hellip; hai b&ecirc;n bờ.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">7.&nbsp;S&ocirc;ng Douro, Bồ Đ&agrave;o Nha</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/ttpPxpqm.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Nằm ở ph&iacute;a bắc Bồ Đ&agrave;o Nha, kh&iacute; hậu &ocirc;n h&ograve;a c&ugrave;ng điều kiện thuận lợi về thổ nhưỡng đ&atilde; rải l&ecirc;n v&ugrave;ng thung lũng Dourno dọc bờ&nbsp;s&ocirc;ng những c&aacute;nh đồng nho xanh tươi m&ecirc;nh m&ocirc;ng.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">D&ugrave; ngay nay&nbsp;khu vực n&agrave;y đ&atilde; ph&aacute;t triển, những bờ s&ocirc;ng bị b&agrave;o m&ograve;n dần, nhưng nhiều thị trấn dọc theo&nbsp;s&ocirc;ng vẫn giữ được &aacute;nh&nbsp;h&agrave;o quang c&ograve;n lại từ thế kỷ 19. Đến đ&acirc;y du kh&aacute;ch c&oacute; cơ hội kh&aacute;m ph&aacute; v&ugrave;ng đất xinh đẹp, y&ecirc;n b&igrave;nh, nơi&nbsp;số lượng c&aacute;c nh&agrave; sản xuất nho bằng với cư d&acirc;n sinh sống.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">8.&nbsp;S&ocirc;ng Neretva, Bosnia v&agrave; Herzegovina</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/8ss1Jdf3.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">S&ocirc;ng Neretva kh&ocirc;ng chỉ &ldquo;th&ocirc;i mi&ecirc;n&rdquo; du kh&aacute;ch bởi vẻ đẹp hoang sơ, l&agrave;n nước xanh như ngọc, m&agrave; c&ograve;n ở ti&ecirc;u chuẩn con s&ocirc;ng sạch nhất ch&acirc;u &Acirc;u, nơi nước của n&oacute; c&oacute; thể uống được.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Neretva bắt nguồn từ trong d&atilde;y n&uacute;i Dinanic, nằm ở độ cao 1.609m so với mực nước biển v&agrave; được mệnh danh l&agrave; một trong những nơi lạnh nhất tr&ecirc;n thế giới.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Du kh&aacute;ch c&oacute; thể đến th&agrave;nh phố Mostar, đứng tr&ecirc;n c&acirc;y cầu đ&aacute; trắng từ thế kỷ 16 chi&ecirc;m ngưỡng vẻ đẹp c&ugrave;a d&ograve;ng s&ocirc;ng hoặc c&oacute; thể tham gia nhảy cầu thể thử cảm gi&aacute;c mạnh.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">9.&nbsp;S&ocirc;ng Danube, ch&acirc;u &Acirc;u</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/HRqrJjSS.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">L&agrave; con s&ocirc;ng d&agrave;i thứ hai ở ch&acirc;u &Acirc;u, xếp sau s&ocirc;ng Volga&nbsp;ở Nga.&nbsp;Danube chảy qua 10&nbsp;quốc gia Romania, Hungary, Serbia, Đức, &Aacute;o, Bulgaria, Slovakia, Croatia, Ukraine v&agrave; Moldova v&agrave; phục vụ đời sống&nbsp;một số th&agrave;nh phố lịch sử nhất của ch&acirc;u &Acirc;u.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Du kh&aacute;ch c&oacute; thể kh&aacute;m ph&aacute; d&ograve;ng s&ocirc;ng theo h&agrave;nh tr&igrave;nh sau: thưởng thức c&agrave; ph&ecirc;&nbsp;ở qu&aacute;n Art Deco (Vienna, &Aacute;o), vi vu qua c&aacute;c thủ đ&ocirc; Budapest (Hungary), Bratislava (Slovakia) v&agrave; dừng ch&acirc;n để trải nghiệm c&aacute;c hoạt động ngo&agrave;i trời ưa th&iacute;ch dưới ch&acirc;n n&uacute;i Carpathians.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">10.&nbsp;S&ocirc;ng Spree, Đức</span></a></span></strong></h3>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/03/11/GqIQS1TD.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Nếu du kh&aacute;ch chọn s&ocirc;ng Rhine l&agrave;m điểm đến ưa th&iacute;ch, th&igrave; Spree cũng kh&ocirc;ng n&ecirc;n bỏ lỡ. Spree chảy qua trung t&acirc;m&nbsp;vương quốc cổ của Saxony, Bohemia v&agrave; Bavaria, nơi c&aacute;c kiến ​​tr&uacute;c từ thời trung cổ vẫn c&ograve;n lưu giữ tốt.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Rong ruổi tr&ecirc;n t&agrave;u dọc s&ocirc;ng Spree, bạn c&oacute; thể chi&ecirc;m ngưỡng vẻ đẹp th&agrave;nh phố Berlin rực rỡ trong nắng v&agrave;ng, những buổi chiều ho&agrave;ng h&ocirc;n lộng gi&oacute; hay lung linh &aacute;nh đ&egrave;n về đ&ecirc;m&hellip; v&agrave; đ&oacute;&nbsp;sẽ l&agrave; một trong những trải nghiệm tuyệt vời nhất.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">11.S&ocirc;ng Nile, Ai Cập</span></a></span></strong></h3>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626/2015/03/12/nT4CLPRH.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">D&agrave;i 6.695km, l&agrave; nơi bắt đầu của nền văn minh Ai Cập, nơi đ&acirc;y vẫn c&ograve;n mang nhiều n&eacute;t đẹp cổ xưa h&ograve;a lẫn&nbsp;trong n&eacute;t hiện đại của cuộc sống ven s&ocirc;ng. Đến đ&acirc;y, du kh&aacute;ch c&oacute; thể đi thuyền&nbsp;3-4 ng&agrave;y qua Aswan v&agrave; Luxor, dừng ch&acirc;n ngắm nh&igrave;n c&aacute;c ng&ocirc;i ch&ugrave;a ở Kom Ombo, Edfu v&agrave; Esna.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Với những kh&aacute;ch th&iacute;ch mạo hiểm, c&oacute; thể kh&aacute;m con&nbsp;s&ocirc;ng bằng c&aacute;ch đi thuyền theo hướng Bắc từ Luxor đến Dendera v&agrave; Abydos. C&oacute; thể mất đến 12 ng&agrave;y để kh&aacute;m ph&aacute; c&aacute;c điểm tham quan dọc bờ s&ocirc;ng như Sohag, El Amarna, Tuna El-Gabal, Ashmunein v&agrave; kết th&uacute;c ở Cairo, thủ đ&ocirc; Ai Cập nhộn nhịp.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">12. S&ocirc;ng Brahmaputra, Trung Quốc/Ấn Độ/Bangladesh</span></a></span></strong></h3>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626/2015/03/12/xaKNJnrH.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Bắt nguồn từ trong n&uacute;i băng ở Angsi, T&acirc;y Tạng (Trung Quốc)&nbsp;chảy qua Arunachal Pradesh (Ấn Độ), sau đ&oacute; chảy dọc theo hướng t&acirc;y nam thung lũng Assam v&agrave; đổ về ph&iacute;a nam Bangladesh.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Đ&acirc;y l&agrave; một trong những tuyến&nbsp;đường thủy nhanh nhất tr&ecirc;n thế giới,&nbsp;c&oacute; cảnh quan đẹp v&agrave; đổi thay hằng giờ với những cồn c&aacute;t ven s&ocirc;ng bồi đắp v&agrave; biến mất sau đ&oacute; do&nbsp;mực nước l&ecirc;n xuống nhanh ch&oacute;ng.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Những h&ograve;n đảo nhỏ c&ugrave;ng với khoảng 200.000 cư d&acirc;n sinh sống dọc bờ s&ocirc;ng sẽ tạo n&ecirc;n bức tranh thi&ecirc;n nhi&ecirc;n tươi đẹp v&agrave; ấn tượng, khiến bạn kh&ocirc;ng khỏi ngỡ ng&agrave;ng.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">13. S&ocirc;ng Chobe, ch&acirc;u Phi</span></a></span></strong></h3>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626/2015/03/12/xLcpC5gF.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Con s&ocirc;ng bắt nguồn ở Angola với&nbsp;t&ecirc;n gọi&nbsp;Kwando, sau đ&oacute;&nbsp;l&agrave; Linyanti ở Botswana, v&agrave;&nbsp;cuối c&ugrave;ng c&oacute; t&ecirc;n Chobe ở Namibia. Đ&acirc;y l&agrave; một trong những d&ograve;ng s&ocirc;ng hoang d&atilde; v&agrave; kỳ lạ nhất tr&ecirc;n thế giới, chứa đựng những gi&aacute; trị nổi bật về hệ động, thực vật đa dạng của ch&acirc;u Phi.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Tr&ecirc;n bi&ecirc;n giới ph&iacute;a bắc của Botswana, hệ thống s&ocirc;ng Kwando, Linyanti v&agrave; Chobe gặp nhau tạo th&agrave;nh một bức tranh sơn thủy hữu t&igrave;nh gồm hồ, đảo, c&aacute;c k&ecirc;nh v&agrave; c&aacute;c v&ugrave;ng ngập lũ.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">C&ocirc;ng vi&ecirc;n quốc gia Chobe cũng rất nổi tiếng với những đ&agrave;n voi, tr&acirc;u với số lượng l&ecirc;n đến h&agrave;ng chục ng&agrave;n con.</span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-nuoc-ngoai-2.html\"><span style=\"color:#c0392b\">14. S&ocirc;ng Hằng, Ấn Độ/Bangladesh</span></a></span></strong></h3>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:null\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626/2015/03/12/360eYHvg.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">S&ocirc;ng Hằng, con s&ocirc;ng linh thi&ecirc;ng nhất ở Ấn Độ, bắt nguồn từ d&atilde;y Himalaya, bang Uttarakhand (Ấn Độ)&nbsp;chảy về ph&iacute;a nam v&agrave; ph&iacute;a đ&ocirc;ng th&ocirc;ng qua hướng bắc của Ấn Độ v&agrave; Bangladesh trước khi đổ ra vịnh Bengal.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Đ&acirc;y kh&ocirc;ng chỉ l&agrave; biểu tượng về n&eacute;t đẹp t&acirc;m linh m&agrave; c&ograve;n l&agrave; nơi sinh sống của h&agrave;ng triệu cư d&acirc;n.</span></span></p>\r\n\r\n<p><span style=\"color:null\"><span style=\"font-size:14px\">Đến đ&acirc;y du kh&aacute;ch c&oacute; thể t&igrave;m hiểu cuộc sống d&acirc;n cư, n&eacute;t cổ k&iacute;nh v&agrave; linh thi&ecirc;ng của th&agrave;nh phố th&aacute;nh Kolkata, Varasani; bắt gặp những h&igrave;nh ảnh h&agrave;ng ng&agrave;n c&aacute;c cầu thang ven s&ocirc;ng, nơi Phật tử đến c&uacute;ng tế thần Shiva.</span></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"color:null\"><span style=\"font-size:14px\"><strong>XU&Acirc;N LỘC (Theo Telegraph)</strong></span></span></p>\r\n', '1521813244SongMeKong.jpg'),
(4, 1, 'Đi du lịch dưới đáy biển Nha Trang', '<p>Đ&acirc;y l&agrave; dịch vụ du lịch mới tại vịnh Nha Trang do C&ocirc;ng ty lặn Holiday tổ chức, được UBND tỉnh Kh&aacute;nh H&ograve;a cho ph&eacute;p&nbsp;</p>\r\n', '<h1 style=\"text-align:center\"><span style=\"color:#2980b9\"><span style=\"font-size:16px\"><strong>Đi du lịch dưới đ&aacute;y biển Nha Trang</strong></span></span></h1>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Nha-Trang-Da-Lat-8.html\"><span style=\"color:#000000\"><img alt=\"\" src=\"http://static.new.tuoitre.vn/tto/i/s626/2015/03/08/ODYhicSh.jpg\" style=\"height:280px; width:670px\" /></span></a></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><span style=\"color:#000000\">&Ocirc;ng Trương Kỉnh - gi&aacute;m đốc Ban quản l&yacute;&nbsp;</span><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Nha-Trang-Da-Lat-8.html\"><span style=\"color:#000000\">vịnh Nha Trang</span></a><span style=\"color:#000000\">&nbsp;- đội mũ, đi dạo dưới đ&aacute;y biển Nha Trang tại H&ograve;n Một - Ảnh do &ocirc;ng Trương Kỉnh cung cấp</span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Theo &ocirc;ng Trương Kỉnh - gi&aacute;m đốc Ban quản l&yacute; vịnh Nha Trang, đ&acirc;y l&agrave; hoạt động rất hay về du lịch v&agrave; bảo vệ m&ocirc;i trường sinh th&aacute;i biển vịnh Nha Trang.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000000\">Tối 8-3, &ocirc;ng L&ecirc; Hồng T&agrave;i - gi&aacute;m đốc C&ocirc;ng ty lặn Holiday - cho biết khu b&egrave; dịch vụ du lịch đi bộ dưới đ&aacute;y&nbsp;</span><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Nha-Trang-Da-Lat-8.html\"><span style=\"color:#000000\">biển Nha Trang&nbsp;</span></a><span style=\"color:#000000\">n&agrave;y đang được đặt tại v&ugrave;ng</span><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Nha-Trang-Da-Lat-8.html\"><span style=\"color:#000000\">&nbsp;biển H&ograve;n Một</span></a><span style=\"color:#000000\">.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Du kh&aacute;ch muốn đi bộ dưới đ&aacute;y biển chỉ cần đội chiếc mũ c&oacute; cung cấp kh&iacute; oxy v&agrave; theo hướng dẫn vi&ecirc;n l&agrave; c&oacute; thể xuống biển để dạo ngắm c&aacute;, san h&ocirc; dưới v&ugrave;ng đ&aacute;y biển rộng khoảng 50m2, từ 15-20 ph&uacute;t/lần.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Chiếc mũ để đội đi dưới đ&aacute;y biển n&agrave;y c&oacute; thiết kế đặc biệt, nặng 40kg nhưng khi đội v&agrave; đi dưới nước chỉ c&ograve;n nặng chừng 4kg.</span></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>theo Phan Song Ng&acirc;n (b&aacute;o tuổi trẻ)</strong></span></span></p>\r\n', '1521813365DayBienNhaTrang.jpg'),
(5, 0, 'Những điểm đến tựa thiên đường của Nhật Bản', '<p>Từ những c&aacute;nh đồng hoa, hồ tr&ecirc;n n&uacute;i cao đến đồi c&aacute;t, l&agrave;ng cổ đều mang đến du kh&aacute;ch cung bậc cảm x&uacute;c kh&aacute;c nhau</p>\r\n', '<h1 style=\"text-align:center\"><span style=\"color:#2980b9\"><span style=\"font-size:16px\"><strong>Những điểm đến tựa thi&ecirc;n đường của Nhật Bản</strong></span></span></h1>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_0\" src=\"http://c0.f34.img.vnecdn.net/2015/03/25/1-1427247867_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\">Vườn hoa tử đằng Kawachi nằm ở tỉnh&nbsp;Fukuoka,</span></a><span style=\"color:#000000\">&nbsp;l&agrave; điểm đến nổi tiếng của&nbsp;</span><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\">Nhật Bản</span></a><span style=\"color:#000000\">&nbsp;v&agrave;o dịp th&aacute;ng 4. Nơi đ&acirc;y trồng khoảng 150 c&acirc;y hoa tử đằng với 20 loại kh&aacute;c nhau. Khi nở rộ, ch&uacute;ng rủ xuống th&agrave;nh đường hầm hoa m&agrave;u trắng t&iacute;m h&uacute;t hồn kh&aacute;ch tham quan.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_1\" src=\"http://c0.f35.img.vnecdn.net/2015/03/25/2-1427247868_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Hồ Happo thuộc tỉnh Nagano&nbsp;được bao bọc bởi c&aacute;c d&atilde;y n&uacute;i v&agrave; nằm ở độ cao 2.060m so với mực nước biển. Với diện t&iacute;ch kh&aacute; nhỏ, Happo sở hữu vẻ đẹp tựa thi&ecirc;n đường&nbsp;bởi mặt hồ phẳng lặng quanh năm soi b&oacute;ng những đỉnh n&uacute;i cao hơn 3.000m.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_2\" src=\"http://c0.f33.img.vnecdn.net/2015/03/25/11-1427247871_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\">C&ocirc;ng vi&ecirc;n ven biển Hitachi&nbsp;</span></a><span style=\"color:#000000\">nằm ở quận Ibaraki nổi tiếng với hơn 4 triệu b&ocirc;ng hoa&nbsp;nemophila. Cuối th&aacute;ng 4, đầu th&aacute;ng 5, khi hoa nở rộ, những ngọn đồi trong c&ocirc;ng vi&ecirc;n phủ một m&agrave;u xanh t&iacute;m ng&uacute;t ng&agrave;n v&agrave; đẹp mắt. Ngo&agrave;i ra, diện t&iacute;ch&nbsp;190 ha của c&ocirc;ng vi&ecirc;n cũng li&ecirc;n tục thay đổi m&agrave;u sắc theo m&ugrave;a.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_3\" src=\"http://c0.f36.img.vnecdn.net/2015/03/25/13-1427247872_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">V&agrave;o th&aacute;ng 2, khi diễn ra lễ hội Con đường &aacute;nh s&aacute;ng tuyết Otaru ở&nbsp;Hokkaido, h&agrave;ng trăm ngọn nến được thắp tr&ecirc;n con k&ecirc;nh trong th&agrave;nh phố. C&ugrave;ng với đ&egrave;n lồng v&agrave; những bức tượng tuyết, nơi đ&acirc;y lung linh như trong truyện cổ t&iacute;ch.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_4\" src=\"http://c0.f34.img.vnecdn.net/2015/03/25/16-1427247872_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\">Tottori-sakyu l&agrave; đồi c&aacute;t lớn nhất Nhật Bản</span></a><span style=\"color:#000000\">&nbsp;với chiều d&agrave;i 16 km, rộng 2 km.&nbsp;Những đụn c&aacute;t ở đ&acirc;y l&agrave; sự pha trộn giữa c&aacute;t v&agrave; tro n&uacute;i lửa trong gần 100.000 năm, sau đ&oacute; được h&igrave;nh th&agrave;nh nhờ những cơn gi&oacute; biển. Du kh&aacute;ch đến tham quan c&oacute; thể trải nghiệm cưỡi lạc đ&agrave; hoặc chơi trượt c&aacute;t.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_5\" src=\"http://c0.f35.img.vnecdn.net/2015/03/25/18-1427247873_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Trang trại Tomita&nbsp;ở Hokkaido c&oacute; 3 nơi trồng hoa oải hương l&agrave; khu ph&iacute;a Đ&ocirc;ng, c&aacute;nh đồng&nbsp;Sakiwai v&agrave; vườn hoa lavender truyền thống. Ri&ecirc;ng c&aacute;nh đồng Sakiwai c&oacute; 4 loại oải hương trồng theo h&agrave;ng, tạo th&agrave;nh một thảm hoa rực rỡ.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_6\" src=\"http://c0.f33.img.vnecdn.net/2015/03/25/21-1427247873_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\">Ainokura l&agrave; ng&ocirc;i l&agrave;ng cổ nổi tiếng của Nhật Bản</span></a><span style=\"color:#000000\">&nbsp;được UNESCO c&ocirc;ng nhận l&agrave; di sản thế giới. Ng&ocirc;i nh&agrave; theo phong c&aacute;ch truyền thống&nbsp;gassho cổ nhất đ&atilde; ở đ&acirc;y khoảng 400 năm. Đến đ&acirc;y, du kh&aacute;ch được chi&ecirc;m ngưỡng nghệ thuật kiến tr&uacute;c của người Nhật xưa với những m&aacute;i nh&agrave; kh&ocirc;ng d&ugrave;ng đinh c&ugrave;ng thiết kế dốc đứng để ngăn sự t&iacute;ch tụ của nước mưa v&agrave; tuyết.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_7\" src=\"http://c0.f36.img.vnecdn.net/2015/03/25/23-1427247874_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Kh&ocirc;ng chỉ sở hữu h&agrave;ng trăm thửa ruộng bậc thang tuyệt đẹp, thị trấn Ini Tanada ở Hiroshima c&ograve;n nổi tiếng bởi những hạt gạo thơm ngon. Nơi đ&acirc;y c&ograve;n thường xuy&ecirc;n tổ chức c&aacute;c sự kiện li&ecirc;n quan đến trồng l&uacute;a, thu h&uacute;t rất đ&ocirc;ng du kh&aacute;ch.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_8\" src=\"http://c0.f33.img.vnecdn.net/2015/03/25/27-1427247874_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000000\">Khu vườn trong Bảo t&agrave;ng nghệ thuật Adachi ở Shimane&nbsp;được coi l&agrave; bức tranh sống động về Nhật Bản v&agrave; l&agrave; một trong những vườn đẹp nhất ở đất nước mặt trời mọc. &Ocirc;ng Adachi Zenko th&agrave;nh lập bảo t&agrave;ng năm 1980 để thỏa niềm đam m&ecirc; với cả&nbsp;</span><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\">nghệ thuật v&agrave; vườn Nhật Bản.</span></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_9\" src=\"http://c0.f36.img.vnecdn.net/2015/03/25/29-1427247875_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Dưới ch&acirc;n n&uacute;i Ph&uacute; Sĩ l&agrave; một thảm hoa với hơn 800.000 c&acirc;y shibazakura đủ m&agrave;u nở rộ gồm hồng, trắng, t&iacute;m. H&agrave;ng năm, khi b&ocirc;ng hoa đầu ti&ecirc;n chớm nở cũng l&agrave; l&uacute;c h&agrave;ng ngh&igrave;n du kh&aacute;ch đổ về khu vực Ngũ Hồ để tham gia lễ hội Shibazakura Ph&uacute; Sĩ. Năm nay, dự kiến b&ocirc;ng hoa đầu ti&ecirc;n sẽ nở v&agrave;o ng&agrave;y 18/4.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Nhat-Ban-32.html\"><span style=\"color:#000000\"><img alt=\"\" id=\"vne_slide_image_10\" src=\"http://c0.f35.img.vnecdn.net/2015/03/25/30-1427247875_660x0.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Vườn&nbsp;Senganen nằm dọc bờ biển ph&iacute;a bắc trung t&acirc;m th&agrave;nh phố Kagoshima. Nơi đ&acirc;y được v&iacute; như một Nhật Bản thu nhỏ với những d&ograve;ng suối, ao nhỏ, đền thờ v&agrave; cả một khu rừng tre. Từ đ&acirc;y,&nbsp;du kh&aacute;ch c&oacute; thể nh&igrave;n ra n&uacute;i lửa Sakurajima v&agrave; vịnh Kagoshima.</span></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Vy An&nbsp;</strong>(theo&nbsp;<em>CNN</em>)</span></span></p>\r\n', '1521813645DiemDenTuaThienDuongNhatBan.jpg');
INSERT INTO `danh_lam_thang_canh` (`pk_id`, `hot`, `ten`, `noi_dung`, `chi_tiet`, `anh`) VALUES
(6, 1, 'Những điểm tham quan hấp dẫn khi đi du lịch Singapore', '<p>Du lich Singapore - Singapore l&agrave; một h&ograve;n đảo c&oacute; h&igrave;nh dạng một vi&ecirc;n kim cương bao quanh bởi nhiều đảo nhỏ kh&aacute;c.</p>\r\n', '<h1 style=\"text-align:center\"><span style=\"color:#2980b9\"><span style=\"font-size:16px\"><strong>Những điểm tham quan hấp dẫn khi đi du lịch Singapore</strong></span></span></h1>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">1. Tượng nh&acirc;n sư Merlion</span></strong></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><strong>Tượng Merlion&nbsp;</strong>lần đầu ti&ecirc;n được thiết kế như l&agrave; một biểu tượng của Hiệp hội Quảng b&aacute; Du lịch Singapore (STPB) v&agrave;o năm 1964 - h&igrave;nh ảnh con th&uacute; đầu sư tử, m&igrave;nh c&aacute; đang cưỡi tr&ecirc;n ngọn s&oacute;ng nhanh ch&oacute;ng trở th&agrave;nh biểu tượng của đảo quốc Singapore tr&ecirc;n thế giới.&nbsp;Tượng Merlion với chiều cao 8,6m v&agrave; trọng lượng 70 tấn được đ&uacute;c bằng xi măng fondue bởi nghệ nh&acirc;n Singapore qu&aacute; cố, &Ocirc;ng Lim Nang Seng. Bức tượng Merlion thứ hai c&oacute; k&iacute;ch thước nhỏ hơn với chiều cao 2m v&agrave; nặng 3 tấn, cũng do nghệ nh&acirc;n n&agrave;y thực hiện. Th&acirc;n tượng được đ&uacute;c bằng xi măng fondue, lớp &aacute;o b&ecirc;n ngo&agrave;i được l&agrave;m từ những chiếc dĩa bằng sứ v&agrave; đ&ocirc;i mắt của Merlion l&agrave; hai t&aacute;ch tr&agrave; nhỏ m&agrave;u đỏ.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>2. Đảo Sentosa</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Santosa c&oacute; nghĩa l&agrave; thanh b&igrave;nh v&agrave; y&ecirc;n tĩnh trong tiếng M&atilde; Lai, l&agrave; một khu nghỉ m&aacute;t đảo ở Singapore, h&agrave;ng năm đ&oacute;n 5 triệu du kh&aacute;ch. C&aacute;c điểm thu h&uacute;t tr&ecirc;n đảo n&agrave;y gồm: b&atilde;i biển d&agrave;i 2 km, ph&aacute;o đ&agrave;i Siloso, hai s&acirc;n golf v&agrave; 2 kh&aacute;ch sạn 5 sao. Đảo n&agrave;y c&oacute; diện t&iacute;ch 5 ki-l&ocirc;-m&eacute;t vu&ocirc;ng, nằm c&aacute;ch bờ biển ph&iacute;a nam của lục địa Singapore 0,5 km. Đ&acirc;y l&agrave; đảo lớn thứ 4 của Singapore (kh&ocirc;ng kể đảo ch&iacute;nh). 70% h&ograve;n đảo n&agrave;y bao phủ rừng trồng, l&agrave; nơi sinh sống của nhiều lo&agrave;i động vật như tắc k&egrave;, khỉ, c&ocirc;ng, vẹt cũng như nhiều loại động thực vật kh&aacute;c. Một phần đ&aacute;ng kể diện t&iacute;ch đảo được tạo ra từ lấn biển.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">&nbsp;Tọa lạc tại Sentosa, Thế giới nước l&agrave; một khu vui chơi tuyệt vời với vẻ đẹp thật ấn tượng của một thế giới ho&agrave;n to&agrave;n kh&aacute;c lạ dưới đại dương. Từ những b&atilde;i biển v&agrave; những hồ cạn c&oacute; nhiều đ&aacute; tảng, du kh&aacute;ch bắt đầu đi xuống dốc s&acirc;u, qua những rặng san h&ocirc; rực rỡ sắc m&agrave;u v&agrave; đời sống thực vật sinh động trước khi đặt ch&acirc;n l&ecirc;n đường hầm hầm di động ngoạn mục bằng nhựa acrylic d&agrave;i 83m, nơi chốn của những đ&agrave;n c&aacute;, c&aacute;c lo&agrave;i d&atilde; th&uacute; r&igrave;nh mồi v&agrave; những cư d&acirc;n đại dương ngoại lai kh&aacute;c.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>3. Batanic Garden</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">&nbsp;Vườn Thực Vật (Batanic Garden) nằm ở Trung t&acirc;m th&agrave;nh phố với diện t&iacute;ch hơn 54ha, nơi đ&acirc;y nổi tiếng bởi việc nghi&ecirc;n cứu, thu thập những lo&agrave;i thực vật v&agrave; những lo&agrave;i hoa cỏ nhiệt đới qu&yacute; hiếm.&nbsp; B&ecirc;n trong vườn c&oacute; rừng nguy&ecirc;n sinh thi&ecirc;n nhi&ecirc;n v&agrave; vườn hoa mu&ocirc;n m&agrave;u mu&ocirc;n sắc rực rỡ một v&ugrave;ng. Nguồn thực vật nơi đ&acirc;y v&ocirc; c&ugrave;ng phong ph&uacute; v&agrave; đa dạng, c&oacute; rất nhiều chủng loại kh&aacute;c nhau. Nơi đ&acirc;y c&ograve;n c&oacute; hồ Hồ Di Đ&igrave;nh l&agrave; một c&ocirc;ng tr&igrave;nh kiến tr&uacute;c kiểu Victoria, nơi trồng hơn 1,2 vạn lo&agrave;i hoa lan qu&yacute; hiếm tr&ecirc;n thế giới. B&ecirc;n trong vườn thực vật Singapore c&ograve;n c&oacute; thư viện lớn, nơi đ&acirc;y c&oacute; ph&ograve;ng ti&ecirc;u bản c&aacute;c lo&agrave;i thực vật nhằm để thu thập v&agrave; cất giữ ti&ecirc;u bản c&aacute;c lo&agrave;i c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>4. C&ocirc;ng vi&ecirc;n bướm &amp; Vương quốc C&ocirc;n tr&ugrave;ng</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Bạn sẽ kinh ngạc trước một thế giới mu&ocirc;n m&agrave;u v&agrave; h&agrave;ng ng&agrave;n c&aacute;nh bướm rập rờn khi tản bộ qua m&ocirc;i trường của những sinh vật mỏng manh n&agrave;y. H&atilde;y đến gần v&agrave; tiếp x&uacute;c với những ch&uacute;a tể của Thế giới C&ocirc;n tr&ugrave;ng - Bọ c&aacute;nh cứng T&ecirc; gi&aacute;c, Nhện Khổng lồ v&agrave; lo&agrave;i b&ograve; cạp &ndash; trong một c&aacute;i hang d&agrave;i 70m, hang c&ocirc;n tr&ugrave;ng đầu ti&ecirc;n tr&ecirc;n thế giới. Khi bạn th&aacute;m hiểm s&acirc;u hơn trong g&oacute;c tối, chỉ c&oacute; &aacute;nh s&aacute;ng đom đ&oacute;m dẫn đườc cho bạn.&nbsp;Với hơn 3.000 lo&agrave;i bướm v&agrave; mẫu c&ocirc;n tr&ugrave;ng, đ&acirc;y l&agrave; một trong những bộ sưu tập lớn nhất thế giới.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>5. Bảo t&agrave;ng S&aacute;p</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Tại bảo t&agrave;ng n&agrave;y, những h&igrave;nh người với k&iacute;ch cỡ như thật, những đồ tạo t&aacute;c qu&yacute; hiếm v&agrave; c&ocirc;ng nghệ tối t&acirc;n sẽ mang những h&igrave;nh ảnh, &acirc;m thanh, hương vị Singapore từ những ng&agrave;y xa xưa trở lại cuộc sống hiện đại. Kh&aacute;ch c&oacute; thể được nghe kể lại chi tiết về lịch sử ng&agrave;nh h&agrave;ng hải Singapore qua đ&ocirc;i mắt một thương gia người Hoa, đối diện với những nh&acirc;n vật uy t&iacute;n như Ng&agrave;i Stamford Raffles, hồi tưởng lại những giờ ph&uacute;t đen tối nhất của Singapore, hoặc đơn giản l&agrave; ăn mừng với người d&acirc;n khi họ t&aacute;i hiện những nghi thức, lễ hội v&agrave; th&ocirc;ng lệ văn h&oacute;a của người Hoa, Malay, Ấn Độ v&agrave; Peranakan trong thời kỳ cực thịnh của họ.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>6. Vườn th&uacute; đ&ecirc;m Night Safari</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Vườn th&uacute; đ&ecirc;m h&agrave;ng đầu n&agrave;y sở hữu tr&ecirc;n 900 con th&uacute; của 135 lo&agrave;i ngoại lai sinh sống trong t&aacute;m khu vực được t&aacute;i tạo tương tự c&aacute;c v&ugrave;ng địa l&yacute; như rừng mưa Đ&ocirc;ng Nam &Aacute;, đồng cỏ nhiệt đới Phi ch&acirc;u, thung lũng s&ocirc;ng Nepal, đồng hoang Nam Mỹ v&agrave; rừng rậm Myanmar.&nbsp;Đi dạo một m&igrave;nh dọc theo đường m&ograve;n đi bộ hay thư gi&atilde;n tr&ecirc;n xe điện &ndash; cho d&ugrave; bạn chọn bất kỳ h&igrave;nh thức n&agrave;o, Night Safari l&agrave; một chuyến phi&ecirc;u lưu hoang d&atilde; kh&ocirc;ng thể bỏ qua.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\">&nbsp;<strong>7.Trung t&acirc;m ăn uống Maxwell</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Nằm ở trung t&acirc;m của khu phố người Hoa, Maxwell khiến du kh&aacute;ch c&oacute; được cảm nhận th&uacute; vị về một trung t&acirc;m ăn uống ngo&agrave;i trời ồn &atilde;, đ&ocirc;ng đ&uacute;c. Mặc d&ugrave; m&oacute;n ăn ở đ&acirc;y được &Acirc;u h&oacute;a nhiều bằng c&aacute;ch cắt giảm c&aacute;c gia vị đặc trưng nhưng Maxwell vẫn l&agrave; lựa chọn tuyệt vời với những m&oacute;n như rotijohn (b&aacute;nh m&igrave; chi&ecirc;n với thịt b&ograve;, trứng v&agrave; đ&ocirc;i khi l&agrave; thịt cừu ăn với nước sốt ớt), rotiprata (b&aacute;nh kếp Ấn Độ ăn với c&agrave; ri).<br />\r\nMaxwell c&oacute; rất nhiều m&oacute;n ăn hải sản hấp dẫn, v&igrave; thế đừng bỏ qua m&oacute;n đầu c&aacute; nấu c&agrave; ri v&agrave; nước dừa ngon ngọt. C&aacute;c loại nước &eacute;p tr&aacute;i c&acirc;y ở đ&acirc;y cũng rất ngon v&agrave; c&oacute; gi&aacute; cả dễ chịu. Đ&acirc;y cũng l&agrave; nơi c&oacute; m&oacute;n tr&agrave; teh tarek thơm ngon v&agrave; m&aacute;t lạnh trong những ng&agrave;y h&egrave; oi bức.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>8. Chợ đ&ecirc;m Chinatown</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">V&agrave;o ban ng&agrave;y, con phố Smith kh&ocirc;ng c&oacute; g&igrave; ấn tượng ngo&agrave;i những quầy h&agrave;ng b&aacute;n đồ kim loại. Tuy nhi&ecirc;n, h&atilde;y trở lại v&agrave;o l&uacute;c ho&agrave;ng h&ocirc;n để thưởng thức những m&oacute;n m&igrave; đặc trưng của người Hoa v&agrave; H&agrave;n Quốc. Chợ đ&ecirc;m Chinatown đặc biệt vui nhộn nếu bạn gh&eacute; v&agrave;o những dịp lễ hội truyền thống như Tết Nguy&ecirc;n Đ&aacute;n với nhiều m&agrave;n biểu diễn sống động h&agrave;ng đ&ecirc;m như m&uacute;a l&acirc;n-sư-rồng, tr&igrave;nh diễn v&otilde; thuật, kinh kịch Trung Hoa v&agrave; đi c&agrave; kheo.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>9</strong>.<strong>Vịnh Gluttons</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Gluttons được coi l&agrave; trung t&acirc;m ẩm thực ngo&agrave;i trời hấp dẫn nhất của Singapore. Địa điểm của Gluttons nằm ngay kế b&ecirc;n nh&agrave; h&aacute;t Esplanade, v&igrave; thế rất tiện cho du kh&aacute;ch sau khi thưởng thức h&ograve;a nhạc. Du kh&aacute;ch đến đ&acirc;y sẽ cho&aacute;ng ngợp bởi v&ocirc; v&agrave;n lựa chọn c&aacute;c m&oacute;n ăn hấp dẫn với gi&aacute; chỉ v&agrave;i đ&ocirc; la Singapore một m&oacute;n. Bạn đừng bỏ qua nasi goring, một m&oacute;n cơm chi&ecirc;n nổi tiếng với h&agrave;nh l&aacute; c&oacute; th&ecirc;m nước sốt đậu n&agrave;nh của Indonesia tạo n&ecirc;n m&ugrave;i vị rất đặc trưng. Ngo&agrave;i ra, bạn c&oacute; thể t&igrave;m thấy hương vị của Singapore cổ xưa trong m&oacute;n m&igrave; căn x&agrave;o g&agrave; thơm ngon nổi tiếng.</span></span></p>\r\n\r\n<h4><span style=\"color:#000000\"><span style=\"font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nguồn: Th&ocirc;ng tin du lịch</span></span></h4>\r\n', '1521813797DiemDenSingapore.jpg'),
(7, 0, '10 điều phải thử khi Du lịch Hà Nội', '<p>H&agrave; nội, thủ đ&ocirc; h&agrave; nội, ch&ugrave;a một cột</p>\r\n', '<h1 style=\"text-align:center\"><span style=\"color:#2980b9\"><span style=\"font-size:16px\"><strong>10 điều phải thử khi Du lịch H&agrave; Nội</strong></span></span></h1>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Dạo Hồ Gươm s&aacute;ng sớm hay tham quan th&agrave;nh phố bằng xe bus chỉ l&agrave; hai trong số 10 điều du kh&aacute;ch n&ecirc;n thử khi gh&eacute; tham quan thủ đ&ocirc; H&agrave; Nội ngh&igrave;n năm văn hiến.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Dưới đ&acirc;y l&agrave; 10 trải nghiệm đ&aacute;ng nhớ của du kh&aacute;ch khi ở H&agrave; Nội.</span></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">1. Đi chợ sớm</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Chợ hoa Quảng B&aacute; v&agrave; chợ đầu mối Long Bi&ecirc;n l&agrave; hai địa điểm du kh&aacute;ch n&ecirc;n gh&eacute; qua một lần khi tới thăm H&agrave; Nội. Sản phẩm ch&iacute;nh ở đ&acirc;y l&agrave; hoa v&agrave; thực phẩm. Chợ được mở gần như suốt đ&ecirc;m. Gh&eacute; qua hai khu chợ n&agrave;y, kh&ocirc;ng chỉ c&oacute; người mua, người b&aacute;n m&agrave; c&ograve;n rất nhiều du kh&aacute;ch, những người muốn t&igrave;m kiếm cho m&igrave;nh một g&oacute;c nh&igrave;n kh&aacute;c về n&eacute;t văn h&oacute;a đặc biệt trong đời sống của con người H&agrave; Nội.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:#000000\"><img alt=\"chợ quảng bá\" src=\"http://vietnamtourism.net.vn/upload/files/2014/tin%20tuc/cho-dem-ha-noi.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">2. B&aacute;ch bộ Hồ Gươm v&agrave;o buổi s&aacute;ng sớm</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Triston Yeo, một nhiếp ảnh gia nổi tiếng kh&aacute; gắn b&oacute; với Việt Nam từng bầu chọn Hồ Gươm buổi s&aacute;ng sớm l&agrave; điều tuyệt vời nhất. Du kh&aacute;ch kh&ocirc;ng chỉ thấy được vẻ đẹp dịu d&agrave;ng, đằm thắm của thi&ecirc;n nhi&ecirc;n, m&agrave; c&ograve;n cả th&oacute;i quen thường nhật của người H&agrave; Nội l&agrave; tập thể dục, bất kể mua đ&ocirc;ng hay h&egrave;. Du kh&aacute;ch c&oacute; thể tản bộ ven hồ để tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh, m&aacute;t mẻ hay nghỉ ngơi tr&ecirc;n một chiếc đ&aacute; để quan s&aacute;t v&agrave; thậm ch&iacute; l&agrave; h&ograve;a v&agrave;o những đ&aacute;m người tập dưỡng sinh, erobic...</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:#000000\"><img alt=\"hồ gươm\" src=\"http://vietnamtourism.net.vn/upload/files/2014/tin%20tuc/cau-the-huc-ho%20guom.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">3. Ngồi ăn ở một qu&aacute;n vỉa h&egrave;</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">H&agrave; Nội nổi tiếng với những g&aacute;nh h&agrave;ng rong v&agrave; c&aacute;c qu&aacute;n c&oacute;c tr&ecirc;n h&egrave; phố. Ai tới thủ đ&ocirc; đều bất ngờ trước đủ loại thực phẩm được b&agrave;y b&aacute;n tại đ&acirc;y, từ những b&aacute;t phở mang đậm hương vị Việt cho tới những m&oacute;n ăn chơi như nem chua r&aacute;n, ph&ocirc; mai que, nước m&iacute;a, v&agrave; thậm ch&iacute; cả những m&oacute;n ăn đ&atilde; l&agrave;m n&ecirc;n c&aacute;i hồn của đất Kinh kỳ như b&aacute;nh cuốn Thanh Tr&igrave; hay b&uacute;n chả... Gh&eacute; thăm H&agrave; Nội v&agrave;o bất kỳ thời điểm n&agrave;o trong năm, du kh&aacute;ch đều c&oacute; thể lựa cho m&igrave;nh những m&oacute;n ăn ngon. H&atilde;y tinh &yacute; t&igrave;m những m&oacute;n ngon theo m&ugrave;a để cảm nhận được đầy đủ ẩm thực H&agrave; Nội tr&ecirc;n những qu&aacute;n c&oacute;c vỉa h&egrave;.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:#000000\"><img alt=\"phở hà nội\" src=\"http://vietnamtourism.net.vn/upload/files/2014/tin%20tuc/pho-ha-noi.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">4. Ở kh&aacute;ch sạn cao tầng ở Hồ T&acirc;y v&agrave; ngắm H&agrave; Nội từ tr&ecirc;n cao</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Du kh&aacute;ch gh&eacute; thăm H&agrave; Nội đừng qu&ecirc;n t&igrave;m cho m&igrave;nh một kh&aacute;ch sạn cao tầng để ngắm th&agrave;nh phố từ tr&ecirc;n cao. Đ&acirc;y l&agrave; một c&aacute;ch kh&ocirc;ng mới nhưng sẽ mang những bất ngờ th&uacute; vị tới mỗi người.&nbsp;T&ugrave;y v&agrave;o thời gian hay địa điểm lựa chọn m&agrave; du kh&aacute;ch c&oacute; thể quan s&aacute;t th&agrave;nh phố v&agrave;o ban ng&agrave;y hoặc buổi tối. Lời khuy&ecirc;n l&agrave; h&atilde;y thử cả hai khoảng thời gian tr&ecirc;n tại c&ugrave;ng một địa điểm để thấy được những n&eacute;t kh&aacute;c biệt.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:#000000\"><img alt=\"hà nội\" src=\"http://vietnamtourism.net.vn/upload/files/2014/tin%20tuc/ha-noi-tren%20cao.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">5. Đi bộ kh&aacute;m ph&aacute; phố cổ</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Phố cổ H&agrave; Nội lu&ocirc;n l&agrave; địa điểm hấp dẫn nhiều du kh&aacute;ch nhất thủ đ&ocirc;. Mặc d&ugrave; với thiết kế b&agrave;n cờ, c&aacute;c con phố song song vu&ocirc;ng g&oacute;c với nhau, nhưng kh&ocirc;ng phải ai cũng dễ d&agrave;ng t&igrave;m được đường ra khi đ&atilde; lạc v&agrave;o &quot;m&ecirc; cung&quot; n&agrave;y. Dạo quanh những con phố nhỏ tấp nập hay gh&eacute; v&agrave;o những cửa h&agrave;ng lưu niệm nằm khuất s&acirc;u trong ng&otilde; nhỏ sẽ l&agrave; những trải nghiệm th&uacute; vị cho bất kỳ du kh&aacute;ch n&agrave;o đặt ch&acirc;n tới nơi đ&acirc;y. Nếu cảm thấy mệt, h&atilde;y t&igrave;m một qu&aacute;n c&agrave; ph&ecirc; nằm tr&ecirc;n ban c&ocirc;ng của một ng&ocirc;i nh&agrave; cổ để quan s&aacute;t cuộc sống n&aacute;o nhiệt ph&iacute;a dưới b&ecirc;n ly c&agrave; ph&ecirc; đậm vị H&agrave; Nội.</span></span></p>\r\n\r\n<h3><span style=\"color:#000000\"><span style=\"font-size:14px\">6. Ra Hồ T&acirc;y để ngắm ho&agrave;ng h&ocirc;n</span></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Nằm ở ph&iacute;a T&acirc;y Bắc của H&agrave; Nội, Hồ T&acirc;y l&agrave; một địa điểm du lịch kh&aacute; h&uacute;t kh&aacute;ch. Nhiều người thường t&igrave;m tới đ&acirc;y để thưởng thức đặc sản chỉ c&oacute; tại nơi n&agrave;y nhưng cũng kh&ocirc;ng &iacute;t người đến để thả m&igrave;nh v&agrave;o kh&ocirc;ng gian rộng lớn rất đỗi y&ecirc;n b&igrave;nh. Đặc biệt khi chiều về, hồ T&acirc;y trở n&ecirc;n huyền ảo trong sắc v&agrave;ng cam nhuộm k&iacute;n về ph&iacute;a ch&acirc;n trời. Du kh&aacute;ch c&oacute; thể dừng ch&acirc;n để lưu lại những tấm h&igrave;nh kỷ niệm ấn tượng.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:#000000\"><img alt=\"hoàng hôn hồ tây\" src=\"http://vietnamtourism.net.vn/upload/files/2014/tin%20tuc/hoang-hon-ho-tay.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">7. Tham quan th&agrave;nh phố bằng xe bus hoặc c&aacute;c xe tour của c&aacute;c h&atilde;ng Du lịch</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Ngo&agrave;i xe điện v&agrave; x&iacute;ch l&ocirc; th&igrave; xe bus ch&iacute;nh l&agrave; phương tiện tiếp theo m&agrave; du kh&aacute;ch n&ecirc;n thử một lần khi đặt ch&acirc;n tới H&agrave; Nội. Kh&ocirc;ng chỉ ghi điểm v&igrave; chi ph&iacute; kh&aacute; rẻ, m&agrave; xe bus c&ograve;n hấp dẫn bởi lộ tr&igrave;nh phong ph&uacute;, đi qua hầu hết c&aacute;c địa điểm du lịch nổi tiếng của H&agrave; Nội. Với hai m&agrave;u sắc bắt mắt l&agrave; đỏ v&agrave; v&agrave;ng, xe bus cũng c&oacute; thể g&oacute;p phần tạo n&ecirc;n điểm nhấn cho những bức h&igrave;nh du lịch của mỗi người.</span></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">8. Tham gia lễ hạ cờ ở lăng B&aacute;c v&agrave;o l&uacute;c 10h đ&ecirc;m</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Nếu kh&ocirc;ng thể dậy sớm chứng kiến nghi lễ k&eacute;o cờ, du kh&aacute;ch h&atilde;y tham gia lễ hạ cờ tại lăng B&aacute;c v&agrave;o 9h tối h&agrave;ng ng&agrave;y. Đ&acirc;y l&agrave; một nghi lễ thi&ecirc;ng li&ecirc;ng được nhiều du kh&aacute;ch trong v&agrave; ngo&agrave;i nước, thậm ch&iacute; cả những người d&acirc;n H&agrave; Nội chờ đợi.&nbsp;C&aacute;c hoạt động tại quảng trường Ba Đ&igrave;nh đều được dừng lại khi loa ph&aacute;t thanh bắt đầu th&ocirc;ng b&aacute;o chuẩn bị diễn ra lễ hạ cờ. L&aacute; cờ Tổ Quốc sẽ được hạ bởi một đo&agrave;n qu&acirc;n gồm 34 chiến sĩ bước đều từ ph&iacute;a b&ecirc;n phải lăng trong nền nhạc &ldquo;B&aacute;c vẫn c&ugrave;ng ch&uacute;ng ch&aacute;u h&agrave;nh qu&acirc;n&rdquo;. Nhiều du kh&aacute;ch khi gh&eacute; thăm H&agrave; Nội đ&atilde; kh&ocirc;ng khỏi bất ngờ khi chứng kiến rất nhiều người d&acirc;n quanh đ&oacute; đứng trang nghi&ecirc;m, d&otilde;i theo l&aacute; cờ đang từ từ được hạ xuống.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:#000000\"><img alt=\"lễ hạ cờ\" src=\"http://vietnamtourism.net.vn/upload/files/2014/tin%20tuc/ha-co%20-lang-bac.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">9. Đi bộ tr&ecirc;n cầu Long Bi&ecirc;n huyền thoại</span></strong></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Kh&ocirc;ng đơn thuần chỉ bắc ngang s&ocirc;ng Hồng, cầu Long Bi&ecirc;n ch&iacute;nh l&agrave; nh&acirc;n chứng lịch sử của H&agrave; Nội, được nhiều du kh&aacute;ch gh&eacute; qua khi đặt ch&acirc;n l&ecirc;n đất thủ đ&ocirc;. C&acirc;y cầu n&agrave;y được x&acirc;y dựng v&agrave;o năm 1898 với 3 l&agrave;n xe, ri&ecirc;ng l&agrave;n giữa d&agrave;nh cho t&agrave;u lửa qua lại. Mang một d&aacute;ng vẻ cổ k&iacute;nh v&agrave; ấn tượng bởi kiến tr&uacute;c, cầu Long Bi&ecirc;n đ&atilde; rất nhiều lần xuất hiện trong c&aacute;c t&aacute;c phẩm của một số nghệ sĩ nước ng&ograve;ai cũng như một số dự &aacute;n của người Việt trẻ. Tới H&agrave; Nội, du kh&aacute;ch h&atilde;y thử đi bộ tr&ecirc;n cầu v&agrave;o s&aacute;ng sớm hay chiều cuối ng&agrave;y. Đ&oacute; l&agrave; sẽ l&agrave; một trải nghiệm th&uacute; vị v&agrave; kh&oacute; c&oacute; thể qu&ecirc;n.</span></span></p>\r\n\r\n<h3><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">10. Kh&aacute;m ph&aacute; H&agrave; Nội về đ&ecirc;m</span></strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Ban ng&agrave;y l&agrave; một th&agrave;nh phố s&ocirc;i động v&agrave; n&aacute;o nhiệt thế nhưng H&agrave; Nội của đ&ecirc;m lại l&agrave; bức tranh ho&agrave;n to&agrave;n tr&aacute;i ngược. C&aacute;c d&atilde;y phố ban ng&agrave;y rực rỡ m&agrave;u sắc của những cửa h&agrave;ng lưu niệm được thay thế bằng sự im l&igrave;m, những khu trung t&acirc;m ồn &atilde; cũng kho&aacute;c l&ecirc;n vẻ b&igrave;nh lặng hiếm c&oacute;. Du kh&aacute;ch c&oacute; thể men theo c&aacute;c con phố, đi dọc những địa điểm m&agrave; trước đ&oacute; vẫn c&ograve;n sầm uất để tận hưởng một kh&ocirc;ng gian kh&aacute;c của H&agrave; Nội.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><span style=\"color:#000000\"><img alt=\"hà-nội-về-đêm\" src=\"http://vietnamtourism.net.vn/upload/files/2014/tin%20tuc/ha-noi-ve-dem.jpg\" style=\"height:280px; width:670px\" /></span></a></span></p>\r\n\r\n<h3 style=\"text-align:right\"><span style=\"color:#000000\"><span style=\"font-size:14px\">&nbsp; &nbsp; &nbsp; Đỗ Huyền Vnexpress</span></span></h3>\r\n', '152181424710DieuDuLichHaNoi.jpg'),
(8, 1, 'Những Điểm tham quan thu hút khách du lịch ở Pattaya - Thái Lan', '<p>Pattaya l&agrave; một điểm đến du lịch h&agrave;ng đầu khi bạn đi du lịch Th&aacute;i Lan, tại đ&acirc;y c&oacute; v&ocirc; số c&aacute;c điểm tham quan</p>\r\n', '<h1 style=\"text-align:center\"><span style=\"color:#2980b9\"><span style=\"font-size:16px\"><strong>Những Điểm tham quan thu h&uacute;t kh&aacute;ch du lịch ở Pattaya - Th&aacute;i Lan</strong></span></span></h1>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>1. Mimosa&nbsp; Pattaya &ndash; City of Love &ndash; Th&agrave;nh phố t&igrave;nh y&ecirc;u</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Mimosa&nbsp; Pattaya &ndash; Th&agrave;nh phố của t&igrave;nh y&ecirc;u được x&acirc;y dựng với kiến tr&uacute;c Ph&aacute;p, m&agrave;u sắc tươi s&aacute;ng v&agrave; được bao quanh bởi 1 bầu kh&ocirc;ng kh&iacute; l&atilde;ng mạn với m&ugrave;i thơm của c&aacute;c loại hoa.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Mimosa Pattaya &nbsp;đi v&agrave;o hoạt động cuối năm 2013, một &yacute; tưởng tuyệt vời v&agrave; chắc chắn sẽ trở th&agrave;nh một điểm thu h&uacute;t du lịch lớn. N&oacute; l&agrave; một trung t&acirc;m c&oacute; gần 300 cửa h&agrave;ng với đầy đủ hương vị c&aacute;c m&oacute;n ăn đặc trưng của đất nước Th&aacute;i Lan v&agrave; nhiều nước kh&aacute;c tr&ecirc;n khắp thế giới.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/03/images.jpg\" style=\"height:280px; width:670px\" /></span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>2. N&uacute;i&nbsp; Phật V&agrave;ng &ndash; Khao Chi Chan</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Khao Chi Chan l&agrave; mKhao Chi Chan l&agrave; một trong những địa điểm du lịch ở Chonburi nơi c&oacute; một bức h&igrave;nh đức Phật lớn được đi&ecirc;u khắc tr&ecirc;n v&aacute;ch đ&aacute; v&agrave; được d&aacute;t v&agrave;ng với chiều c&aacute;o 109 m&eacute;t v&agrave; rộng 70 m&eacute;t. Xung quanh l&agrave; những vườn đ&aacute; thi&ecirc;n nhi&ecirc;n v&agrave; c&oacute; một hồ nước lớn ph&iacute;a trước.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/03/The-Marina-Bay-Sands-Top-Singapore.jpg\" style=\"height:280px; width:670px\" /></span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>3.Mini Siam Pattaya &ndash; Cửa sổ hế giới thu nhỏ</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Tại Mini Siam, qu&yacute; kh&aacute;ch sẽ được gh&eacute; thăm 1 thủ đ&ocirc; Paris mộng mơ của nước Ph&aacute;p v&agrave; chi&ecirc;m ngưỡng th&aacute;p Eiffel, Khải Ho&agrave;n M&ocirc;n sau đ&oacute; sang đất nước Italia với Th&aacute;p nghi&ecirc;ng Pisa, tiếp đến l&agrave; s&acirc;n vận động Closseo ở Roma, nghe nhạc cổ điển trong nh&agrave; h&aacute;t Opera ở Sydney, &hellip;.Đến với Mini Siam, du kh&aacute;ch sẽ được chi&ecirc;m ngưỡng c&aacute;c di sản của thể thế giới trong chuyến du lịch Th&aacute;i Lan của m&igrave;nh.</span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><span style=\"font-size:14px\"><strong>4.Bảo t&agrave;ng nghệ thuật 3D Paradise</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">Với diện t&iacute;ch 6000m2, bảo t&agrave;ng cũng l&agrave; một điểm đến thu h&uacute;t du kh&aacute;ch, tại đ&acirc;y qu&yacute; kh&aacute;ch được thưởng thức một thế giới tranh 3D vui nhộn, độc đ&aacute;o, si&ecirc;u thực, h&agrave;i hước v&agrave; v&ocirc; c&ugrave;ng đa dạng về thể loại. Từ những bức tranh phong cảnh cho đến những bức tranh k&yacute; họa h&igrave;nh ảnh con người.</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Nguồn: Th&ocirc;ng tin du lịch</strong></span></span></p>\r\n', '1521814390ThaiLan.jpg');

