INSERT INTO `du_lich_ngoai_nuoc` (`pk_id`, `fk_danh_muc`, `hot`, `ten`, `thoi_gian`, `khoi_hanh`, `fk_tim_kiem_khoi_hanh`, `ngay_khoi_hanh`, `gia_cu`, `gia_moi`, `tim_kiem_gia`, `giam_gia`, `so_cho_ngoi`, `anh`, `mau_nen`, `lich_trinh`) VALUES
(1, 1, 1, 'DU LỊCH THÁI LAN TỪ HÀ NỘI: KHÁM PHÁ CHỢ NỔI, BUFFET 86 TẦNG VÀ BUFFET TRÁI CÂY (6N/5Đ)', '6 ngày 5 đêm', 'Hà Nội', 2, 'Liên hệ', 10500000, 9200000, 'Từ 5 - 10 triệu', 'GIẢM 12%', 5, '1521788873ThaiLan_KhamPhaChoNoi_BUFET.png', 'inner01', '<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 01: H&Agrave; NỘI &ndash; BANGKOK (ĂN TỐI)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/e/b/e/9/5/ebe95536e10c36fc125e22e1fc2275aa.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">13h30: Xe v&agrave; Hướng dẫn vi&ecirc;n C&ocirc;ng ty ch&uacute;ng t&ocirc;i sẽ đ&oacute;n qu&yacute; kh&aacute;ch tại Nh&agrave; H&aacute;t Lớn ở H&agrave; Nội bắt đầu&nbsp;<a href=\"http://vietnamtourism.net.vn/\">tour Du lịch Th&aacute;i Lan</a>, Sau khi khởi h&agrave;nh ra s&acirc;n bay quốc tế Nội B&agrave;i qu&yacute; kh&aacute;ch sẽ đ&aacute;p chuyến bay QR 829 (16.35 &ndash; 18.50) &nbsp;đi Bangkok, Đo&agrave;n tới s&acirc;n bay, ăn tối v&agrave; nghỉ đ&ecirc;m tại Bangkok.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 02: BANGKOK &ndash; PATTAYA ( ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/e/d/a/9/c/eda9c7154a0c1efebcdab64dbb8b0a14.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute; Đoàn khởi hành đi Pattaya - thành ph&ocirc;́ nghỉ mát đẹp n&ocirc;̉i ti&ecirc;́ng vùng Đ&ocirc;ng Nam Á. Tr&ecirc;n đường quý khách ghé thăm Siracha Tiger Zoo-xem chương trình bi&ecirc;̉u di&ecirc;̃n của các chú cá s&acirc;́u và h&ocirc;̉.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tr&ecirc;n đường qu&yacute; kh&aacute;ch tham quan Trung t&acirc;m v&agrave;ng bạc đ&aacute; qu&yacute; lớn nhất Đ&ocirc;ng Nam &Aacute; - nơi di&ecirc;̃n ra các phi&ecirc;n trao đ&ocirc;̉i mua bán đá quý đạt chứng chỉ ISO 9002 v&ecirc;̀ quản lý ch&acirc;́t lượng, nơi trưng b&agrave;y c&aacute;c loại đ&aacute; qu&yacute; đẹp nổi tiếng, cung cấp những th&ocirc;ng tin bổ &iacute;ch về qu&aacute; tr&igrave;nh khai th&aacute;c, chế t&aacute;c đ&aacute; qu&yacute; tại Th&aacute;i Lan v&agrave; tr&ecirc;n thế giới. Thăm Vườn Bướm &ndash; qu&yacute; kh&aacute;ch sẽ được thưởng thức v&agrave; nghe giới thiệu những sản phẩm đặc biệt của miền Nam Thailand (Mật Ong, phấn Hoa, Tổ Yến&hellip;). Đo&agrave;n Ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn Pattaya: Town in Town hotel 3*** hoặc tương đương.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 03: PATTAYA ( ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/2/7/0/8/8/27088121d38fec5fa3c07db2053ef3f1.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi ăn s&aacute;ng tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u cao tốc thăm Đảo San H&ocirc;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Qu&yacute; kh&aacute;ch c&oacute; thể tham dự c&aacute;c tr&ograve; chơi như: d&ugrave; bay, c&acirc;u c&aacute;, lướt v&aacute;n, l&aacute;i Scooter tr&ecirc;n mặt biển, th&aacute;m hiểm dưới đ&aacute;y biển&hellip;(chi ph&iacute; tự t&uacute;c). Q&uacute;y kh&aacute;ch tự do tắm biển Pattaya với l&agrave;n nước trong xanh, b&atilde;i c&aacute;t d&agrave;i trắng mịn. Trở lại đ&acirc;́t li&ecirc;̀n Quý khách ăn trưa tại nh&agrave; H&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Buổi chi&ecirc;̀u q&uacute;y kh&aacute;ch tham quan Chợ Nổi 4 Miền, du thuyền ngắm cảnh v&agrave; tham quan n&uacute;i Phật V&agrave;ng, Vườn Nho Silve Lake. Q&uacute;y kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng v&agrave; xem Alcashow ho&agrave;nh tr&aacute;ng nhất Th&aacute;i Lan. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn Pattaya: Town in Town hotel 3*** hoặc tương đương.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 04: PATTAYA &ndash; BANGKOK ( ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/2/3/9/b/1/239b123a7a5d203ce2efa7ffc3bd5912.PNG\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau bữa sáng tại khách sạn, Q&uacute;y kh&aacute;ch trả ph&ograve;ng kh&aacute;ch sạn. Xe khởi hành đưa Quý khách trở lại Bangkok. Quý khách ghé thăm Safari World &ndash; vườn th&uacute; mở tự nhi&ecirc;n lớn nhất ch&acirc;u &Aacute;,với các ti&ecirc;́t mục như: Đười ươi, C&aacute; heo &amp; Cowboy và khu giải trí Marine Park- nơi t&acirc;̣p trung các loại thú quý hi&ecirc;́m tr&ecirc;n th&ecirc;́ giới, xem các chương trình bi&ecirc;̉u đi&ecirc;̃n đặc bi&ecirc;̣t với các loài thú. Q&uacute;y kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng. Q&uacute;y kh&aacute;ch Thăm trại Rắn các ti&ecirc;́t mục bắt rắn đ&ocirc;̣c bằng tay kh&ocirc;ng, sau đó Quý khách được nghe các dược sỹ giới thi&ecirc;̣u v&ecirc;̀ các dược ph&acirc;̉m đặc trị đi&ecirc;̀u ch&ecirc;́ từ loài rắn đ&ocirc;̣c. Cửa h&agrave;ng đồ da. Chiều: Q&uacute;y kh&aacute;ch tham quan Cung Điện M&ugrave;a H&egrave; &ndash; Ng&ocirc;i ch&ugrave;a thờ tượng phật bằng v&agrave;ng lớn nhất thế giới v&agrave; Ch&ugrave;a Phật V&agrave;ng v&agrave; tự do mua bán tại si&ecirc;u thị Tokyu hoặc Big C, Robinson. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn Grand Ville Hotel 3*** hoặc tương đương.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 05: MUA SẮM TỰ DO Ở BANGKOK ( ĂN S&Aacute;NG)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/1/a/9/a/c/1a9aceb842fe7297a04c252fd1efaac2.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau ăn s&aacute;ng buffet tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch tự do mua sắm tại c&aacute;c Trung t&acirc;m mua sắm lớn của Th&aacute;i Lan như:&nbsp;<strong>Big C, World trade centre, Silom</strong>,&hellip;Qu&yacute; kh&aacute;ch c&oacute; thể tự mua những suất ăn theo kiểu Coupon tại c&aacute;c trung t&acirc;m mua sắm n&agrave;y</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 06: BANGKOK &ndash; H&Agrave; NỘI (ĂN S&Aacute;NG)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/0/4/9/e/b/049eb8a15530f778cf1d85d95ae2af78.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi buffet s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n trả ph&ograve;ng kh&aacute;ch sạn, khởi h&agrave;nh tham quan&nbsp;<em><strong>Trung T&acirc;m trưng b&agrave;y c&aacute;c loại thuộc da</strong></em>&nbsp;nổi tiếng của<strong>&nbsp;Th&aacute;i Lan.&nbsp;</strong>Sau đ&oacute; xe đưa Qu&yacute; kh&aacute;ch ra s&acirc;n bay&nbsp;<em><strong>Survanahumi</strong></em>&nbsp;đ&aacute;p chuyến bay<strong>&nbsp;QR 828 (13h15 &ndash; 14h55)&nbsp;</strong>hoặc<strong>&nbsp;VJ8902 (16h10 &ndash; 18h05)&nbsp;</strong>về H&agrave; Nội<strong>.&nbsp;</strong>Kết th&uacute;c&nbsp;<a href=\"http://vietnamtourism.net.vn/\">chuyến Du lịch th&aacute;i lan</a>. Chia tay đo&agrave;n v&agrave; hẹn gặp lại.&nbsp;&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR TRỌN G&Oacute;I GH&Eacute;P ĐO&Agrave;N:</strong></span></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">-6.690.000 VNĐ/01 KH&Aacute;CH.</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR BAO GỒM:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-V&eacute; m&aacute;y bay khứ hồi H&Agrave; NỘI-TH&Aacute;I LAN-H&Agrave; NỘI.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Thuế s&acirc;n bay v&agrave; phụ ph&iacute; xăng dầu khoảng 119 usd ***</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Kh&aacute;ch sạn ti&ecirc;u chuẩn 3 sao, 2 người/01 ph&ograve;ng, nếu lẻ sẽ ngủ 03.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-C&aacute;c bữa ăn theo chương tr&igrave;nh 100 bath/01 bữa ch&iacute;nh, ăn s&aacute;ng tại kh&aacute;ch sạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Ph&iacute; tham quan v&agrave;o cửa 01 lần.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Xe vận chuyển đời mới , m&aacute;y lạnh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;HDV tiếng Việt đi c&ugrave;ng đo&agrave;n suốt tuyến, nhiệt t&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-HDV tại Th&aacute;i Lan nhiệt t&igrave;nh, kinh nghiệm, n&oacute;i Tiếng Việt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Bảo hiểm Du lịch Quốc Tế. Mũ v&agrave; vỏ hộ chiếu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR KH&Ocirc;NG BAO GỒM:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Điện thoại , giặt l&agrave; v&agrave; c&aacute;c chi ph&iacute; kh&aacute;c ngo&agrave;i chương tr&igrave;nh</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Tiền tip cho l&aacute;i xe v&agrave; hdv địa phương l&agrave; 03$/01 ng&agrave;y/01 kh&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;Chi ph&iacute; charge khi kh&ocirc;ng tham gia c&aacute;c điểm mua sắm của ho&agrave;ng gia Th&aacute;i lan.</span></p>\r\n'),
(2, 1, 1, 'DU LỊCH THÁI LAN 30/4-1/5 TỪ TP. HỒ CHÍ MINH', '5 ngày 4 đêm', 'Tp. Hồ Chí Minh', 3, 'Liên hệ', 5990000, 5990000, 'Từ 5 - 10 triệu', '0', 9, '1521790947ThaiLan_30_4_TuHCM.jpg', 'inner03', '<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">ĐẶC ĐIỂM NỔI BẬT CỦA CHƯƠNG TR&Igrave;NH DU LỊCH TH&Aacute;I LAN</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/THAI%20LAN/du-lich-thai-lan-gia-re-5-ngay-4-dem-tu-ha-noi.png\" style=\"height:250px; width:670px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/Du-Lich-ThaiLan1.jpg\" style=\"width:670px\" /></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 01: TP. HỒ CH&Iacute; MINH - BANGKOK</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hướng dẫn vi&ecirc;n sẽ đ&oacute;n đo&agrave;n từ s&acirc;n bay&nbsp;<strong>T&acirc;n Sơn Nhất</strong>. Sau&nbsp;đ&oacute; l&agrave;m thủ tục&nbsp;cho qu&yacute; kh&aacute;ch đ&aacute;p chuyến bay đi<strong><a href=\"http://vietnamtourism.net.vn/danh-muc/Du-lich-Thai-Lan-23.html\">Du lịch Th&aacute;i Lan</a></strong>. Đến vương quốc Th&aacute;i Lan xe đưa Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối với m&oacute;n lẩu suki soup. Sau đ&oacute; về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi hoặc tự do kh&aacute;m ph&aacute; Bangkok về đ&ecirc;m&hellip;&nbsp; &nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"lẩu thái\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/am-thuc-thai-lan-1024x768.jpg\" style=\"height:300px; width:670px\" /></a>&nbsp; &nbsp; &nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 02: BANGKOK &ndash; TIGER ZOO &ndash; PATTAYA</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn, trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh xuống th&agrave;nh phố Pattaya.Tr&ecirc;n đường gh&eacute; tham quan Trại Cọp Tiger Zoo &ndash; Sriracha: tại đ&acirc;y qu&yacute; kh&aacute;ch sẽ xem được c&aacute;c chương tr&igrave;nh biểu diễn như Heo l&agrave;m to&aacute;n, Show C&aacute; S&acirc;u v.v&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/cong-vien-sirracha-tiger-zoo-ve-tham-quan.jpeg\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đo&agrave;n d&ugrave;ng cơm trưa tại nh&agrave; h&agrave;ng địa phương. Đến th&agrave;nh phố Ma Quỷ Pattaya. Qu&yacute; kh&aacute;ch tiếp tục l&ecirc;n ngọn Đồi Đ&ocirc; Đốc Hải Qu&acirc;n &ndash; tại đ&acirc;y qu&yacute; kh&aacute;ch c&oacute; thể cầu b&igrave;nh an cho gia đ&igrave;nh, người th&acirc;n v&agrave; bạn b&egrave;. Ngo&agrave;i ra, qu&yacute; kh&aacute;ch c&ograve;n c&oacute; thể ngắm nh&igrave;n to&agrave;n cảnh Pattaya. Qu&yacute; kh&aacute;ch thưởng thức 01 suất Massage Th&aacute;i Cổ Truyền &ndash; đ&aacute;nh thức tiềm năng cơ thể.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/massage-chan-tua-thai-lan-vietjet-air%20(1).jpg\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">D&ugrave;ng cơm tối với m&oacute;n ăn tự chọn BBQ Hải Sản, qu&yacute; kh&aacute;ch về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi. Tự do kh&aacute;m ph&aacute; th&agrave;nh phố ma quỷ về đ&ecirc;m.</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 03: PATTAYA &ndash; ĐẢO CORAL</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn, qu&yacute; kh&aacute;ch khởi h&agrave;nh đến&nbsp;<strong>Đảo Caral</strong>&nbsp;bằng t&agrave;u cao tốc &ndash; tại đ&acirc;y qu&yacute; kh&aacute;ch tự t&uacute;c tắm biển v&agrave; tự do tham gia c&aacute;c tr&ograve; chơi tr&ecirc;n biển như: nhảy d&ugrave;, l&aacute;i m&ocirc; t&ocirc; nước, trượt v&aacute;n v.v&hellip; Đặc biệt đặc sắc nhất l&agrave; chương tr&igrave;nh lặn biển ngắm san h&ocirc; (Chi ph&iacute; tự t&uacute;c).</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/thailan1.jpg\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đến giờ hẹn qu&yacute; kh&aacute;ch trở về đất liền, d&ugrave;ng cơm trưa. Sau đ&oacute; Tham Quan</strong><strong>&nbsp;Trung T&acirc;m V&agrave;ng Bạc Đ&aacute; Qu&yacute; &ndash;&nbsp;</strong>Một trung t&acirc;m trưng b&agrave;y đ&aacute; qu&yacute; đạt ti&ecirc;u chuẩn ISO 9001.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Viếng thăm&nbsp;<strong>N&uacute;i Phật V&agrave;ng</strong>&nbsp;<strong>(Khao Chee Chan)&nbsp;</strong><strong>-&nbsp;</strong>nổi tiếng với h&igrave;nh đức Phật khắc v&agrave;ng tr&ecirc;n lưng n&uacute;i. Tiếp tục tham quan&nbsp;<strong>Vườn Nho Silver Lake</strong>, một c&aacute;nh đồng nho rộng lớn với rất nhiều hoa, c&oacute; cả vườn bầu khổng lồ, b&iacute; khổng lồ. Ph&iacute;a b&ecirc;n ngo&agrave;i c&oacute; một cửa h&agrave;ng lớn b&aacute;n c&aacute;c sản phẩm l&agrave;m từ nho, nước &eacute;p nho, kẹo nho, mứt nho...</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/vuon-nho-side-lake.jpg\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tham quan&nbsp;<strong>T&ograve;a Nh&agrave; Tỷ Ph&uacute; Sukhawadee</strong>&nbsp;&ndash; tại đ&acirc;y qu&yacute; kh&aacute;ch sẽ tham quan tại khu vực thảm đỏ được trưng b&agrave;y c&aacute;c vật phẩm qu&yacute; gi&aacute; l&agrave;m bằng v&agrave;ng như tượng phật, c&aacute;c h&igrave;nh ảnh của Ho&agrave;ng Tộc v.v...D&ugrave;ng cơm tối mới m&oacute;n buffet tự chọn tại&nbsp;<strong>T&ograve;a Nh&agrave; Tỷ Ph&uacute;</strong>. Qu&yacute; kh&aacute;ch đến tham quan show chuyển đối giới t&iacute;nh&nbsp;<strong>Alcazar Show</strong>&nbsp;- đầy sắc sảo của c&aacute;c vũ c&ocirc;ng Lady Boy với m&agrave;n tr&igrave;nh diễn hấp dẫn v&agrave; đầy sắc m&agrave;u.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/tua-thai-lan-gia-re-tu-ha-noi-show.png\" style=\"height:300px; width:670px\" /></a></span><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/show-al-1024x572.jpg\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 04: PATTAYA &ndash; BANGKOK</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn, trả ph&ograve;ng. Khởi h&agrave;nh trở về thủ đ&ocirc; Bangkok. Đến&nbsp;<strong>cửa h&agrave;ng b&aacute;n c&aacute;c loại thổ sản Th&aacute;i</strong>&nbsp;để d&ugrave;ng thử v&agrave; mua sắm.&nbsp;<strong>Trung t&acirc;m đồ da. Trung t&acirc;m nghi&ecirc;n cứu nọc độc rắn. Vườn bướm.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đo&agrave;n d&ugrave;ng cơm trưa tại&nbsp;<strong>nh&agrave; h&agrave;ng xoay 86 tầng</strong>&nbsp;&ndash; t&ograve;a nh&agrave; Baiyoke Sky cao nhất Th&aacute;i Lan, tại đ&acirc;y qu&yacute; kh&aacute;ch c&oacute; thể thưởng thức hơn 100 m&oacute;n ăn &Acirc;u &ndash; &Aacute; thật phong ph&uacute; v&agrave; hấp dẫn, đồng thời qu&yacute; kh&aacute;ch c&ograve;n thấy được mạng lười giao th&ocirc;ng chằn chịt tại Bangkok.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/baiyok-sky-86-tang.jpg\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đo&agrave;n viếng&nbsp;<strong>Phật Bốn Mặt</strong>&nbsp;nổi tiếng linh thi&ecirc;ng nhất Bangkok, để cầu phước cầu b&igrave;nh an cho gia đ&igrave;nh.Tham quan&nbsp;<strong>Ch&ugrave;a Phật V&agrave;ng</strong>&nbsp;lớn nhất thế giới: cao 3 m&eacute;t v&agrave; nặng hơn 5,5 tấn. Tượng được đ&uacute;c theo phong c&aacute;ch Sukhothai tĩnh lặng v&agrave; được kh&aacute;m ph&aacute; một c&aacute;ch t&igrave;nh cờ v&agrave;o thập ni&ecirc;n 1950 Người địa phương cho rằng bức tượng lớn nhất thế giới n&agrave;y biểu thị cho sự thịnh vượng v&agrave; thuần khiết cũng như sức mạnh v&agrave; quyền năng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/tuong-phat-vang.jpg\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Qu&yacute; kh&aacute;ch tự do mua sắm tại khu trung t&acirc;m Bangkok như:&nbsp;<strong>Big C Patunam, Central World, MBK, Patinum</strong>v.v&hellip; Đến giờ hẹn, xe đưa đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.&nbsp;<strong>(Qu&yacute; kh&aacute;ch tự t&uacute;c ăn tối)</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/du-lich-thai-lan-05.png\" style=\"height:300px; width:670px\" /></a></strong></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 05: BANGKOK &ndash; TP.HỒ CH&Iacute; MINH</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn, trả ph&ograve;ng. Sau đ&oacute; đo&agrave;n tham quan:&nbsp;<strong>Wat Yannawa</strong>&nbsp;- do vị vua thứ V x&acirc;y dựng. Wat Yannava c&oacute; nghĩa l&agrave; ch&ugrave;a thuyền, ng&ocirc;i ch&ugrave;a nằm ở đường Charoen krung quận Sathon Bangkok &ndash; Th&aacute;i Lan.. H&igrave;nh d&aacute;ng ch&ugrave;a được x&acirc;y dựng theo kiểu kiến tr&uacute;c h&igrave;nh d&aacute;ng con thuyền của người Trung Hoa c&ugrave;ng với kiến tr&uacute;c Th&aacute;i l&agrave; c&aacute;c m&aacute;i cao v&uacute;t mang đậm phong c&aacute;ch thời Ayuthaya. Wat Yannawa c&oacute; tuổi đời cả trăm năm. Ngay trước ch&ugrave;a l&agrave; m&ocirc; h&igrave;nh con t&agrave;u thủy để ghi lại c&ocirc;ng cuộc bang giao h&agrave;ng hải của Th&aacute;i với c&aacute;c nước l&acirc;n bang.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/muong-boran_du-lich-thai-lan.png\" style=\"height:300px; width:670px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/banner/thai%20lan/cuoi-voi-thai-lna-1024x440.jpg\" style=\"height:300px; width:670px\" />Dạo Thuyền tr&ecirc;n d&ograve;ng s&ocirc;ng Huyền thoại Chaophraya</strong>,&nbsp;<strong>Xem thuyền Rồng của nh&agrave; vua</strong>, xem hiện tượng c&aacute; nổi tr&ecirc;n s&ocirc;ng.Xe đưa qu&yacute; kh&aacute;ch ra s&acirc;n bay d&ugrave;ng cơm trưa. Sau đ&oacute; đo&agrave;n l&agrave;m thủ tục đ&aacute;p chuyến bay trở về TP.HCM. Đến s&acirc;n bay T&acirc;n Sơn Nhất, chương tr&igrave;nh kết th&uacute;c chuyến&nbsp;<a href=\"http://vietnamtourism.net.vn/\"><strong>di du lịch th&aacute;i lan</strong></a>. Hẹn gặp lại qu&yacute; kh&aacute;ch!</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">GI&Aacute; TOUR BAO GỒM:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">V&eacute; m&aacute;y bay h&agrave;ng kh&ocirc;ng&nbsp;<strong>Jetstar Pacific</strong>&nbsp;khứ hồi theo đo&agrave;n ( SGN &ndash; BKK &ndash; SGN ).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thuế phi trường hai nước, ph&iacute; An ninh, ph&iacute; xăng dầu. (Gi&aacute; Thuế 147$ &aacute;p dụng theo thời điểm xuất v&eacute; ).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn nghỉ tham quan, xe đưa đ&oacute;n theo chương tr&igrave;nh tham quan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&aacute;ch sạn ti&ecirc;u chuẩn 3-4 sao Th&aacute;i Lan. (hoặc kh&aacute;ch sạn tương đương)</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bangkok : S.D Avenue, Dynasty</strong><strong>, Royal River, Lamtama Hotel, Metro Piont v.v&hellip;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Pattaya&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;</strong><strong>Royal Century, Siam Platinum, The Night, LK Paragon, Memo, v.v&hellip;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trưởng đo&agrave;n v&agrave; hướng dẫn vi&ecirc;n tiếng việt suốt tuyến.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tặng n&oacute;n v&agrave; bao da hộ chiếu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ph&iacute; bảo hiểm tai nạn du lịch ( mức bồi thường cao nhất về người l&agrave; 10.000 USD/kh&aacute;ch).</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">GI&Aacute; TOUR KH&Ocirc;NG BAO GỒM:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hộ chiếu phải c&ograve;n hạn tr&ecirc;n 06 th&aacute;ng ( phải c&ograve;n nguy&ecirc;n vẹn, kh&ocirc;ng chỉnh sửa ).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">H&agrave;nh l&yacute; qu&aacute; cước qui định ( Theo quy định l&agrave; 20kg ( gửi ) + 7kg ( x&aacute;ch tay )/kh&aacute;ch ).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xe đưa đ&oacute;n s&acirc;n bay T&acirc;n Sơn Nhất v&agrave; c&aacute;c show diễn về đ&ecirc;m tại Th&aacute;i Lan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute;c chi ph&iacute; c&aacute; nh&acirc;n (ghế d&ugrave; tại đảo Coral, tiền bồi dưỡng cho người massage, ph&iacute; điện thoại, giặt ủi, ăn uống ngo&agrave;i chương tr&igrave;nh, ph&iacute; khu&acirc;n v&aacute;c h&agrave;nh l&yacute;, &hellip;)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiền t&iacute;p hướng dẫn vi&ecirc;n v&agrave; t&agrave;i xế địa phương&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp;<strong>350.000 VNĐ/kh&aacute;ch (~ 15 USD/ kh&aacute;ch)</strong><strong>.</strong></span></p>\r\n'),
(3, 1, 1, 'DU LỊCH THÁI LAN TỪ HÀ NỘI: BANGKOK – PATTAYA (TOUR 4 SAO 5N/4Đ)', '5 ngày 4 đêm', 'Hà Nội', 2, '31/1; 8, 25/3, 24/4', 6760000, 5999000, 'Từ 5 - 10 triệu', 'GIẢM 11%', 8, '1521791202ThaiLan_TuHN_BangKoc.jpg', 'inner02', '<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 01: H&Agrave; NỘI &ndash; BANGKOK (ĂN TỐI)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/2/4/d/4/0/24d40aed0fb2093c8cbf0dd4eb17a0de.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">13h30: Xe v&agrave; Hướng dẫn vi&ecirc;n C&ocirc;ng ty ch&uacute;ng t&ocirc;i sẽ đ&oacute;n qu&yacute; kh&aacute;ch tại Nh&agrave; H&aacute;t Lớn ở H&agrave; Nội, khởi h&agrave;nh ra s&acirc;n bay quốc tế Nội B&agrave;i đ&aacute;p chuyến bay QR 829 (16.35 &ndash; 18.50) &nbsp;đi Bangkok, Đo&agrave;n tới s&acirc;n bay, ăn tối v&agrave; nghỉ đ&ecirc;m tại Bangkok</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 02: BANGKOK &ndash; PATTAYA ( ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/b/9/1/f/b/b91fb663bddb8b3b596449b7e63d8baa.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute; Đoàn khởi hành đi Pattaya - thành ph&ocirc;́ nghỉ mát đẹp n&ocirc;̉i ti&ecirc;́ng vùng Đ&ocirc;ng Nam Á. Tr&ecirc;n đường quý khách ghé thăm Siracha Tiger Zoo-xem chương trình bi&ecirc;̉u di&ecirc;̃n của các chú cá s&acirc;́u và h&ocirc;̉.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tr&ecirc;n đường qu&yacute; kh&aacute;ch thăm Qu&yacute; kh&aacute;ch thăm quan Trung t&acirc;m v&agrave;ng bạc đ&aacute; qu&yacute; lớn nhất Đ&ocirc;ng Nam &Aacute; - nơi di&ecirc;̃n ra các phi&ecirc;n trao đ&ocirc;̉i mua bán đá quý đạt chứng chỉ ISO 9002 v&ecirc;̀ quản lý ch&acirc;́t lượng, nơi trưng b&agrave;y c&aacute;c loại đ&aacute; qu&yacute; đẹp nổi tiếng, cung cấp những th&ocirc;ng tin bổ &iacute;ch về qu&aacute; tr&igrave;nh khai th&aacute;c, chế t&aacute;c đ&aacute; qu&yacute; tại Th&aacute;i Lan v&agrave; tr&ecirc;n thế giới. Thăm Vườn Bướm &ndash; qu&yacute; kh&aacute;ch sẽ được thưởng thức v&agrave; nghe giới thiệu những sản phẩm đặc biệt của miền Nam Thailand (Mật Ong, phấn Hoa, Tổ Yến&hellip;). Đo&agrave;n Ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn Pattaya: Town in Town hotel 3*** hoặc tương đương.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 03: PATTAYA ( ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/7/2/3/7/e/7237ed619b7c13dce2670c9bf800bd3d.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi ăn s&aacute;ng tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u cao tốc thăm Đảo San H&ocirc;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Qu&yacute; kh&aacute;ch c&oacute; thể tham dự c&aacute;c tr&ograve; chơi như: d&ugrave; bay, c&acirc;u c&aacute;, lướt v&aacute;n, l&aacute;i Scooter tr&ecirc;n mặt biển, th&aacute;m hiểm dưới đ&aacute;y biển&hellip;(chi ph&iacute; tự t&uacute;c). Q&uacute;y kh&aacute;ch tự do tắm biển Pattaya với l&agrave;n nước trong xanh, b&atilde;i c&aacute;t d&agrave;i trắng mịn. Trở lại đ&acirc;́t li&ecirc;̀n Quý khách ăn trưa tại nh&agrave; H&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Buổi chi&ecirc;̀u q&uacute;y kh&aacute;ch tham quan Chợ Nổi 4 Miền, du thuyền ngắm cảnh v&agrave; tham quan n&uacute;i Phật V&agrave;ng, Vườn Nho Silve Lake. Q&uacute;y kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng v&agrave; xem Alcashow ho&agrave;nh tr&aacute;ng nhất Th&aacute;i Lan. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn Pattaya: Town in Town hotel 3*** hoặc tương đương.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 04: PATTAYA &ndash; BANGKOK ( ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/5/a/0/d/e/5a0deb4806dfc954e69e252fa8f873ea.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau bữa sáng tại khách sạn, Q&uacute;y kh&aacute;ch trả ph&ograve;ng kh&aacute;ch sạn. Xe khởi hành đưa Quý khách trở lại Bangkok. Quý khách ghé thăm Safari World &ndash; vườn th&uacute; mở tự nhi&ecirc;n lớn nhất ch&acirc;u &Aacute;,với các ti&ecirc;́t mục như: Đười ươi, C&aacute; heo &amp; Cowboy và khu giải trí Marine Park- nơi t&acirc;̣p trung các loại thú quý hi&ecirc;́m tr&ecirc;n th&ecirc;́ giới, xem các chương trình bi&ecirc;̉u đi&ecirc;̃n đặc bi&ecirc;̣t với các loài thú. Q&uacute;y kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng. Q&uacute;y kh&aacute;ch Thăm trại Rắn các ti&ecirc;́t mục bắt rắn đ&ocirc;̣c bằng tay kh&ocirc;ng, sau đó Quý khách được nghe các dược sỹ giới thi&ecirc;̣u v&ecirc;̀ các dược ph&acirc;̉m đặc trị đi&ecirc;̀u ch&ecirc;́ từ loài rắn đ&ocirc;̣c. Cửa h&agrave;ng đồ da. Chiều: Q&uacute;y kh&aacute;ch tham quan Cung Điện M&ugrave;a H&egrave; &ndash; Ng&ocirc;i ch&ugrave;a thờ tượng phật bằng v&agrave;ng lớn nhất thế giới v&agrave; Ch&ugrave;a Phật V&agrave;ng v&agrave; tự do mua bán tại si&ecirc;u thị Tokyu hoặc Big C, Robinson. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn Grand Ville Hotel 3*** hoặc tương đương.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 05: BANGKOK &ndash; H&Agrave; NỘI ( ĂN S&Aacute;NG)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/8/5/8/4/3/8584362daa05a654d9eb620b1962ee88.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn, sau đ&oacute; qu&yacute; kh&aacute;ch trả ph&ograve;ng. Xe đưa Qu&yacute; kh&aacute;ch s&acirc;n bay Suvarnabhumi đ&aacute;p chuyến bay QR 828 ( 13.15 -15.10) về Việt Nam, chia tay v&agrave; hẹn gặp lại Q&uacute;y Kh&aacute;ch, kết th&uacute;c chuyến<a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Thai-Lan-23.html\">&nbsp;Du lịch Th&aacute;i Lan từ H&agrave; Nội.</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR GH&Eacute;P ĐO&Agrave;N:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-GI&Aacute; TRỌN G&Oacute;I:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- 31/1: 5.990.000 VNĐ/01 KH&Aacute;CH.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- 8,25/3; 24/4: 6.290.000 VNĐ/01 KH&Aacute;CH.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR BAO GỒM:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-V&eacute; m&aacute;y bay khứ hồi H&Agrave; NỘI-TH&Aacute;I LAN-H&Agrave; NỘI, QR</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Thuế s&acirc;n bay v&agrave; phụ ph&iacute; xăng dầu khoảng 119 usd ***</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Kh&aacute;ch sạn ti&ecirc;u chuẩn 3 sao, 2 người/01 ph&ograve;ng, nếu lẻ sẽ ngủ 03.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-C&aacute;c bữa ăn theo chương tr&igrave;nh 100 bath/01 bữa ch&iacute;nh, ăn s&aacute;ng tại kh&aacute;ch sạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Ph&iacute; tham quan v&agrave;o cửa 01 lần.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Xe vận chuyển đời mới , m&aacute;y lạnh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;HDV tiếng Việt đi c&ugrave;ng đo&agrave;n suốt tuyến, nhiệt t&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-HDV tại Th&aacute;i Lan nhiệt t&igrave;nh, kinh nghiệm, n&oacute;i Tiếng Việt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;-Bảo hiểm Du lịch Quốc Tế. Mũ v&agrave; vỏ hộ chiếu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR KH&Ocirc;NG BAO GỒM:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Điện thoại , giặt l&agrave; v&agrave; c&aacute;c chi ph&iacute; kh&aacute;c ngo&agrave;i chương tr&igrave;nh</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Tiền tip cho l&aacute;i xe v&agrave; hdv địa phương l&agrave; 03$/01 ng&agrave;y/01 kh&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;Chi ph&iacute; charge khi kh&ocirc;ng tham gia c&aacute;c điểm mua sắm của ho&agrave;ng gia Th&aacute;i lan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>QU&Yacute; KH&Aacute;CH LƯU &Yacute;:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Thủ tục: hộ chiếu thời hạn sử dụng tr&ecirc;n 06 th&aacute;ng t&iacute;nh từ ng&agrave;y khởi h&agrave;nh</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Gi&aacute; tr&ecirc;n c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o ng&agrave;y khởi h&agrave;nh của kh&aacute;ch. Gi&aacute; c&oacute; thể thay đổi khi h&agrave;ng kh&ocirc;ng tăng phụ ph&iacute; nhi&ecirc;n liệu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Chương tr&igrave;nh v&agrave; giờ bay c&oacute; thể thay đổi t&ugrave;y theo ng&agrave;y khởi h&agrave;nh cụ thể.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Chương tr&igrave;nh c&oacute; thể thay đổi lịch tr&igrave;nh thăm quan để ph&ugrave; hợp với điều kiện chuyến bay nhưng c&aacute;c đi&ecirc;m thăm quan vẫn giữ nguy&ecirc;n.</span></p>\r\n'),
(4, 1, 0, 'DU LỊCH THÁI LAN: PHUKET,FREE AND EASY (4N/3Đ)', '4 ngày 3 đêm', 'Hà Nội', 2, 'Liên hệ', 5548000, 5548000, 'Từ 5 - 10 triệu', '0', 7, '1521791378ThaiLan_Phuket.jpg', 'inner04', '<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 01: H&Agrave; NỘI &ndash; PHUKET (ĂN TR&Ecirc;N M&Aacute;Y BAY, ĂN TỐI)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/0/c/0/2/f/0c02f6bdc028cc251d07eabf101d5e70.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-Buổi s&aacute;ng:&nbsp;</strong>Qu&yacute; kh&aacute;ch c&oacute; mặt tại s&acirc;n bay Nội b&agrave;i đ&aacute;p chuyến bay khởi h&agrave;nh đi Phuket bắt đầu chuyến&nbsp;<a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Thai-Lan-23.html\">Du lịch Th&aacute;i Lan</a>. 11h00 đến s&acirc;n bay, xe đ&oacute;n qu&yacute; kh&aacute;ch về kh&aacute;ch sạn tại Pattaya nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-Buổi chiều:&nbsp;</strong>Qu&yacute; kh&aacute;ch tự do dạo chơi v&agrave; tắm biển Pattaya. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 02: THAM QUAN ĐẢO PHI PHI (ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/b/d/f/4/5/bdf45eaf536e852f5c5f7b85aa76c730.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-Buổi s&aacute;ng:</strong>&nbsp;&nbsp; Sau bữa ăn s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch đi can&ocirc; cao tốc ra thăm đảo Korah. Qu&yacute; kh&aacute;ch tự do tắm biển hoặc mua v&eacute; tham gia c&aacute;c tr&ograve; chơi tr&ecirc;n đảo như: lặn biển xem san h&ocirc; dưới đ&aacute;y biển, nhẩy d&ugrave; bay tr&ecirc;n biển.&nbsp; 11h00 can&ocirc; đưa đo&agrave;n về đất liền. Ăn trưa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-Buổi chiều:</strong>&nbsp;Qu&yacute; kh&aacute;ch tự do vui chơi v&agrave; tắm biển.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-Buổi tối:&nbsp;&nbsp;</strong>Qu&yacute; kh&aacute;ch xem biểu diễn văn nghệ Alcazar đặc sắc do c&aacute;c Bede chuyển giới biểu diễn. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 03: TỰ DO KH&Aacute;M PH&Aacute; PHUKET (ĂN S&Aacute;NG)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-Buổi s&aacute;ng:</strong>&nbsp;Qu&yacute; kh&aacute;ch&nbsp;ăn s&aacute;ng tại kh&aacute;ch sạn. Tự do cả ng&agrave;y tham quan đảo Phuket v&agrave; tắm biển.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 04: PHUKET &ndash; H&Agrave; NỘI (ĂN S&Aacute;NG, ĂN TR&Ecirc;N M&Aacute;Y BAY)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/3/2/2/8/4/32284f0cdff18d4cf665d8977c6c85a2.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-Buổi s&aacute;ng:</strong>&nbsp;Sau bữa s&aacute;ng tại kh&aacute;ch sạn, qu&yacute; kh&aacute;ch tự do tham quan cho đến khi xe đ&oacute;n qu&yacute; kh&aacute;ch ra s&acirc;n bay đ&aacute;p chuyến bay về H&agrave; Nội. Về đến s&acirc;n bay Nội B&agrave;i, qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện về nội th&agrave;nh. Kết th&uacute;c chương tr&igrave;nh&nbsp;<a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Thai-Lan-23.html\">tour Du lịch th&aacute;i lan</a>.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR BAO GỒM:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-V&eacute; m&aacute;y bay khứ hồi H&agrave; Nội&nbsp;- Phuket &ndash; H&agrave; Nội (trong v&ograve;ng 4 ng&agrave;y)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Kh&aacute;ch sạn ti&ecirc;u chuẩn 3 sao v&agrave; 4 sao, 02 người/ph&ograve;ng, lẻ người nghỉ ph&ograve;ng 3 người.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-C&aacute;c bữa ăn s&aacute;ng tại kh&aacute;ch sạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Xe đưa đ&oacute;n s&acirc;n bay &ndash; kh&aacute;ch sạn &ndash; s&acirc;n bay tại Phuket ng&agrave;y đến v&agrave; ng&agrave;y đi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOURS BAO GỒM:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Điện thoại , giặt l&agrave; v&agrave; c&aacute;c chi ph&iacute; kh&aacute;c ngo&agrave;i chương tr&igrave;nh</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Tiền tip cho l&aacute;i xe v&agrave; hdv địa phương l&agrave; 03$/01 ng&agrave;y/01 kh&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Chi ph&iacute; charge khi kh&ocirc;ng tham gia c&aacute;c điểm mua sắm của ho&agrave;ng gia Th&aacute;i lan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>LƯU &Yacute;:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Thủ tục: hộ chiếu thời hạn sử dụng tr&ecirc;n 06 th&aacute;ng t&iacute;nh từ ng&agrave;y khởi h&agrave;nh</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Gi&aacute; tr&ecirc;n c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o ng&agrave;y khởi h&agrave;nh của kh&aacute;ch. Gi&aacute; c&oacute; thể thay đổi khi h&agrave;ng kh&ocirc;ng tăng phụ ph&iacute; nhi&ecirc;n liệu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Chương tr&igrave;nh v&agrave; giờ bay c&oacute; thể thay đổi t&ugrave;y theo ng&agrave;y khởi h&agrave;nh cụ thể.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Chương tr&igrave;nh c&oacute; thể thay đổi lịch tr&igrave;nh tham quan để ph&ugrave; hợp với điều kiện chuyến bay nhưng c&aacute;c đi&ecirc;m tham quan vẫn giữ nguy&ecirc;n.</span></p>\r\n');
INSERT INTO `du_lich_ngoai_nuoc` (`pk_id`, `fk_danh_muc`, `hot`, `ten`, `thoi_gian`, `khoi_hanh`, `fk_tim_kiem_khoi_hanh`, `ngay_khoi_hanh`, `gia_cu`, `gia_moi`, `tim_kiem_gia`, `giam_gia`, `so_cho_ngoi`, `anh`, `mau_nen`, `lich_trinh`) VALUES
(5, 1, 1, 'DU LỊCH THÁI LAN - BUFFET 86 TẦNG, ALCAZASHOW, BBQ HÀN QUỐC, DỊCH VỤ 4 SAO(5N/4Đ)', '5 ngày 4 đêm', 'Tp. Hồ Chí Minh', 3, '5,12,19,26/9', 7590000, 7590000, 'Từ 5 - 10 triệu', '0', 9, '1521791638ThaiLan.jpg', 'inner03', '<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 01 : TP.HCM &ndash; BANGKOK (ĂN NHẸ)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/f/1/d/1/e/f1d1edb70f447d605d8afa6eef78b0e3.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trưởng đo&agrave;n của&nbsp;C&ocirc;ng ty Du lịch ch&uacute;ng t&ocirc;i sẽ đ&oacute;n qu&yacute; kh&aacute;ch tại&nbsp;s&acirc;n bay T&acirc;n Sơn Nhất,&nbsp; l&agrave;m thủ tục để đ&aacute;p chuyến bay&nbsp;đi Th&aacute;i Lan bắt đầu chuyến Du lịch Th&aacute;i Lan,&nbsp;Đến&nbsp;vương quốc&nbsp;<a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Thai-Lan-23.html\">Du lịch Thai Lan</a>&nbsp;xe đưa Qu&yacute; kh&aacute;ch về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi hoặc tự do kh&aacute;m ph&aacute; Bangkok về đ&ecirc;m&hellip;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 02: BANGKOK &ndash; S&Ocirc;NG CHAOPHRAYA &ndash; HO&Agrave;NG CUNG &ndash; SIRACHA TIGER ZOO &ndash; TH&Aacute;I MASSAGE &ndash; PATTAYA (ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/f/6/2/9/7/f6297f76303621f4cc25a9a737f0c8cf.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dạo thuyền tr&ecirc;n d&ograve;ng&nbsp;s&ocirc;ng Chaophraya Huyền thoại.&nbsp;Xem thuyền Rồng của nh&agrave; vua, xem hiện tượng c&aacute; nổi tr&ecirc;n s&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tham quan&nbsp;Ho&agrave;ng Cung Ngũ Thế&nbsp;Ho&agrave;ng, được&nbsp;vua Rama V&nbsp;x&acirc;y dựng v&agrave;o năm 1901, v&agrave; từ đ&oacute; đến nay vẫn lu&ocirc;n được bảo tồn cẩn thận để quảng b&aacute; sự huy ho&agrave;ng v&agrave; thịnh vượng của một thời k&igrave; ho&agrave;ng kim.- Đ&acirc;y l&agrave; t&ograve;a nh&agrave; bằng gỗ Teak m&agrave;u v&agrave;ng đẹp v&agrave; lớn nhất thế giới, trong&nbsp;Ho&agrave;ng Cung&nbsp;trưng b&agrave;y c&aacute;c t&aacute;c phẩm như:&nbsp;Ng&agrave; voi &ndash; Thủy Tinh &ndash; Đồ vật bằng bạc &ndash; Đồ s&agrave;nh sứ &ndash; Đồ cổ&hellip;. Tại đ&acirc;y Qu&yacute; kh&aacute;ch c&oacute; thể hiểu r&otilde; hơn về c&aacute;ch sinh hoạt của Ho&agrave;ng thất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp tục Đo&agrave;n tham quan Siracha Tiger Zoo &ndash; l&agrave; nơi duy nhất tr&ecirc;n thế giới thuần dưỡng ch&uacute;a sơn l&acirc;m trở n&ecirc;n th&acirc;n thiện với con người. Qu&yacute; kh&aacute;ch c&oacute; dịp xem show biểu diễn xiếc của những ch&uacute; cọp, khỉ, c&aacute; sấu, đặc biệt vui nhộn với cuộc thi heo học to&aacute;n, heo chạy đua&hellip;. (D&ugrave;ng cơm trưa tại c&ocirc;ng vi&ecirc;n)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau đ&oacute; xe đi thẳng về&nbsp;th&agrave;nh phố &nbsp;Pattaya, khi đến nơi Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối, về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi hoặc tham gia c&aacute;c show về đ&ecirc;m&nbsp;tại Pataya&nbsp;(chi ph&iacute; qu&yacute; kh&aacute;ch tự t&uacute;c)&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hướng dẫn tiếp tục đưa Qu&yacute; kh&aacute;ch đến Massage Th&aacute;i cỗ truyền để thư gi&atilde;n (60 ph&uacute;t)</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 03: PATTAYA-ĐẢO CORAL-NONOOCH-KHAU CHEE CHAN - SHOW ALCAZAR (ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/2/8/5/6/8/28568506c434433d816c9f0242546ec2.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi ăn s&aacute;ng, qu&yacute; kh&aacute;ch khởi h&agrave;nh đi Đảo San H&ocirc; (Coral) Bằng can&ocirc; cao tốc, tại đ&acirc;y Qu&yacute; kh&aacute;ch c&oacute; thể tắm biển hay tham gia c&aacute;c tr&ograve; chơi tr&ecirc;n biển như: D&ugrave; k&eacute;o, Lướt v&aacute;n, L&aacute;i Can&ocirc;, Th&aacute;m hiểm dưới đ&aacute;y biển (Chi ph&iacute; tự t&uacute;c). T&agrave;u Can&ocirc; sẽ đưa Qu&yacute; kh&aacute;ch trở về Pataya tắm rửa v&agrave; d&ugrave;ng cơm trưa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp đến tham quan l&agrave;ng d&acirc;n tộc Nonooch, với h&agrave;ng ng&agrave;n lo&agrave;i Hoa Lan đẹp nhất tr&ecirc;n thế giới v&agrave; c&aacute;c lo&agrave;i giống c&acirc;y hiếm tr&ecirc;n thế giới được nhập v&ecirc; Th&aacute;i Lan gieo trồng với gi&aacute; h&agrave;ng chục ng&agrave;n usd, v&agrave; xem show diễn t&aacute;i hiện lại cuộc chiến giữa Th&aacute;i Lan v&agrave; Miến Điện. Du kh&aacute;ch sẽ được tận hưởng cảm x&uacute;c vui nhộn với show Voi, (voi l&agrave;m xiếc vơi gần 30ch&uacute; voi lớn nhỏ đ&aacute; b&oacute;ng, phi ti&ecirc;u, chơi bowling, dancing) rất th&uacute; vị. Đặc biệt l&agrave; voi vẽ tranh&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp tục tham quan Khau Chee Chan, với h&igrave;nh Th&iacute;ch Ca d&aacute;t v&agrave;ng cao 118 m&eacute;t, nơi được tạo n&ecirc;n nh&acirc;n dịp kỷ niệm 50 năm đăng cơ của nh&agrave; vua Rama thứ 9 của Th&aacute;i Lan&hellip;Sau đ&oacute; xe đưa Qu&yacute; kh&aacute;ch ăn tối với m&oacute;n BBQ Hải Sản thật phong ph&uacute;.&nbsp;Sau đ&oacute; Qu&yacute; kh&aacute;ch sẽ thưởng thức chương tr&igrave;nh biểu diễn ca m&uacute;a nhạc ho&agrave;nh tr&aacute;ng do c&aacute;c vũ c&ocirc;ng chuyển đổi giới t&iacute;nh thục hiện&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 04: PATTAYA -TRUNG T&Acirc;M Đ&Aacute; QU&Yacute; &amp; RẮN ĐỘC-BUFFET 86 TẦNG- BANGKOK (ĂN BA BỮA)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/e/e/5/b/f/ee5bf0f19326b2f0822131734c84d86c.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sauk hi ăn s&aacute;ng, xe đưa Qu&yacute; kh&aacute;ch khởi h&agrave;nh về Bangkok, Tr&ecirc;n đường về gh&eacute; thăm q Vườn Bướm (Butterfly Garden), nơi trưng bầy sản phẩm nổi tiếng 3 miền của vương quốc Th&aacute;i Lan v&agrave; t&igrave;m hiểu về Cao Hồ Cốt. Sau đ&oacute; Qu&yacute; kh&aacute;ch tham quan xưởng chế t&aacute;c đ&aacute; qu&yacute; (Một trung t&acirc;m trưng b&agrave;y đ&aacute; qu&yacute; phong thủy đạt ti&ecirc;u chuẩn ISO 9001) Th&aacute;i Lan được biết đến l&agrave; một quốc gia nổi tiếng khai th&aacute;c về đ&aacute; qu&yacute;. Tại đ&acirc;y Qu&yacute; kh&aacute;ch c&oacute; thể t&igrave;m hiểu m&agrave;u đ&aacute; theo th&aacute;ng sinh của m&igrave;nh.&nbsp;Xe đưa kh&aacute;ch đi ăn trưa Buffet nh&agrave; h&agrave;ng 86 tầng BaiYoke Sky với c&aacute;c m&oacute;n ăn thịnh soạn v&agrave; ngắm nh&igrave;n to&agrave;n cảnh trung t&acirc;m Bangkok. Đo&agrave;n tiếp tục tham quan Trung t&acirc;m nghi&ecirc;n cứu rắn độc. Gh&eacute; tham quan của h&agrave;ng trưng b&agrave;y c&aacute;c loại đồ Da như da C&aacute; Sấu, C&aacute; Đuối&hellip;. Đo&agrave;n ăn tối lẩu suki, sau đ&oacute; vể kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 05: BANGKOK- CH&Ugrave;A PHẬT V&Agrave;NG-CH&Ugrave;A THUYỀN WAT YANNAWA (ĂN S&Aacute;NG)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"http://vietnamtourism.net.vn/upload/tour/2/0/9/7/d/2097d2e1da7f3555338f1adb9906ba2d.jpg\" style=\"float:right; height:150px; width:200px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi ăn s&aacute;ng, xe đưa Qu&yacute; kh&aacute;ch thăm quan ch&ugrave;a Phật V&agrave;ng lớn nhất thế giới: cao 3 m&eacute;t v&agrave; nặng hơn 5 tấn. Tượng được đ&uacute;c theo phong c&aacute;ch Sukhothai tĩnh lặng v&agrave; được kh&aacute;m ph&aacute; một c&aacute;ch t&igrave;nh cờ v&agrave;o thập ni&ecirc;n 1950 Người địa phương cho rằng bức tượng lớn nhất thế giới n&agrave;y biểu thị cho sự thịnh vượng v&agrave; thuần khiết cũng như sức mạnh v&agrave; quyền năng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp đến đo&agrave;n tham quan Ch&ugrave;a Thuyền &ndash; Wat Yan Nawa l&agrave; ng&ocirc;i ch&ugrave;a độc nhất v&ocirc; nhị v&igrave; với h&igrave;nh d&aacute;ng thuyền rồng nằm b&ecirc;n cạnh d&ograve;ng s&ocirc;ng v&agrave;o đời vua Rama III &ndash; ch&ugrave;a c&oacute; lịch sữ l&acirc;u đời linh thi&ecirc;ng ở địa phương,&nbsp;c&ugrave;ng với kiến tr&uacute;c Th&aacute;i l&agrave; c&aacute;c&nbsp;Chedi&nbsp;cao v&uacute;t mang đậm phong c&aacute;ch thời&nbsp;Ayuthaya, Qu&yacute; kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng nhiều X&aacute; Lợi Phật v&agrave; cầu phước cầu b&igrave;nh an cho gia đ&igrave;nh&hellip;Tiếp đến xe đưa Qu&yacute; Kh&aacute;ch ra s&acirc;n bay đ&aacute;p chuyến bay trở về TP.HCM v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href=\"http://vietnamtourism.net.vn/danh-muc/Du-Lich-Thai-Lan-23.html\">du lịch Th&aacute;i Lan</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR TRỌN G&Oacute;I GH&Eacute;P ĐO&Agrave;N:</strong></span></span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:633px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><strong>&nbsp;NG&Agrave;Y KHỞI H&Agrave;NH</strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><strong>GI&Aacute; TOUR</strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><strong>H&Atilde;NG BAY</strong></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\">5,12,19,26/9</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\">6.890.000 VNĐ</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\">&nbsp;TK69: SGN-BKK 20:50 &ndash; 22:20<br />\r\n			TK68: BKK-SGN 16:45 &ndash; 18:15</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR BAO GỒM:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">-V&eacute; m&aacute;y bay khứ hồi</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Thuế phi trường hai nước, ph&iacute; An ninh, phụ ph&iacute; xăng dầu. (Gi&aacute; Thuế được &aacute;p dụng&nbsp; theo thời điểm xuất v&eacute; ).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Ăn nghỉ thăm quan theo chương tr&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Kh&aacute;ch sạn ti&ecirc;u chuẩn 4 sao .</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Xe đưa đ&oacute;n theo chương tr&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-N&oacute;n v&agrave; bao hộ chiếu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Trưởng đo&agrave;n v&agrave; hướng dẫn vi&ecirc;n suốt tuyến.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Ph&iacute; bảo hiểm tai nạn ( mức bồi thường cao nhất về người l&agrave; 10.000 USD/kh&aacute;ch).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">GI&Aacute; TOUR KH&Ocirc;NG BAO GỒM:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Hộ chiếu phải c&ograve;n hạn tr&ecirc;n 06 th&aacute;ng ( phải c&ograve;n nguy&ecirc;n vẹn, kh&ocirc;ng chỉnh sửa ).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-H&agrave;nh l&yacute; qu&aacute; cước qui định.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-C&aacute;c chi ph&iacute; c&aacute; nh&acirc;n (ph&iacute; điện thoại, giặt ủi, ăn uống ngo&agrave;i chương tr&igrave;nh, ph&iacute; khu&acirc;n v&aacute;c h&agrave;nh l&yacute;, &hellip;)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Tiền t&iacute;p hướng dẫn vi&ecirc;n v&agrave; t&agrave;i xế địa phương:&nbsp; 320.000 VNĐ/kh&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Ph&iacute; visa nhập cảnh Việt Nam (VK + NN) : 1.260.000 VNĐ/kh&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Ph&ograve;ng đơn phụ thu: 2.310.000 VNĐ/kh&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-Chi ph&iacute; ph&aacute;t sinh nếu chuyến bay bị huỷ trong trường hợp bất khả kh&aacute;ng: thi&ecirc;n tai, thời tiết, đ&igrave;nh c&ocirc;ng.</span></p>\r\n'),
(6, 2, 0, 'DU LỊCH HỒNG KÔNG 30/4-1/5 TỪ TP. HỒ CHÍ MINH', '4 ngày 3 đêm', 'Tp. Hồ Chí Minh', 3, '2,3,4,7 Tết 2018', 22990000, 20990000, 'Trên 20 triệu', 'GIẢM 9%', 22, '1521792115HongKong_30_4.jpg', 'inner04', '<p style=\"text-align:center\"><span style=\"color:#c0392b\"><strong>CHƯƠNG TR&Igrave;NH ĐẶC SẮC CỦA CHUYẾN DU LỊCH HỒNG K&Ocirc;NG</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><strong>HONGKONG - SHOPPING</strong><br />\r\n<strong>TP. HỒ CH&Iacute;&nbsp; MINH - HỒNG K&Ocirc;NG - SHOPPING TOUR</strong>&nbsp;<img alt=\"KM\" src=\"https://tour.dulichvietnam.com.vn/uploads/hapdan(2)(15).gif\" style=\"height:17px; width:60px\" title=\"KM\" /><br />\r\n<strong>Thời Gian</strong>: 04 NG&Agrave;Y 03 Đ&Ecirc;M -&nbsp;<strong>Phương Tiện</strong>: M&Aacute;Y BAY</p>\r\n\r\n<p><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/Km(23).png\" style=\"height:300px; width:670px\" /></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 01: TP. HỒ CH&Iacute; MINH &ndash; HỒNG K&Ocirc;NG (M&Ugrave;NG 2 TẾT)</strong></span></p>\r\n\r\n<p>Buổi tối qu&yacute; kh&aacute;ch c&oacute; mặt tại ga đi quốc tế - S&acirc;n bay T&acirc;n Sơn Nhất, hướng dẫn vi&ecirc;n đ&oacute;n đo&agrave;n l&agrave;m thủ tục tục xuất cảnh, đ&aacute;p chuyến bay đi&nbsp;<strong><a href=\"http://vietnamtourism.net.vn/\">du lịch Hong Kong.</a></strong></p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/vietnam_airline.jpg\" style=\"height:260px; width:670px\" /></a></p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/dinh-thai-binh-hong-kong-2.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 02: HONG KONG ( M&Ugrave;NG 3 TẾT)</strong></span></p>\r\n\r\n<p>Đến phi trường Chek Lap Kok, xe v&agrave; hướng dẫn vi&ecirc;n đ&oacute;n đo&agrave;n đưa đo&agrave;n đi ngang qua Cầu Thanh M&atilde; &ndash; c&acirc;y cầu d&acirc;y văng c&oacute; nhịp lớn thứ 7 tr&ecirc;n thế giới, một trong những c&acirc;y cầu đẹp nhất Ch&acirc;u &Aacute;. Đo&agrave;n khởi h&agrave;nh tham quan:Chụp h&igrave;nh b&ecirc;n ngo&agrave;i th&aacute;p đồng hồ Clock Tower &ndash; cao 44m nơi đ&acirc;y được coi l&agrave; một cột mốc đ&aacute;ng nhớ cho c&aacute;c h&agrave;ng triệu người nhập cư Trung Quốc.Di sản 1881 &ndash; đ&acirc;y l&agrave; trụ sở của cảnh s&aacute;t biển HongKong từ những năm&nbsp; 1880 -&nbsp; 1996.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/cau-thanh-ma.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p>Đại lộ ng&ocirc;i sao (Tsim Sha Tsui Waterfront) - nơi in dấu tay của c&aacute;c nghệ sĩ nổi tiếng của Hong Kong v&agrave; Trung Quốc, được m&ocirc; phỏng theo Đại Lộ danh vọng Hollywood. Khu vực n&agrave;y nh&igrave;n được to&agrave;n cảnh 44 t&ograve;a nh&agrave; chọc trời hai b&ecirc;n cảng Victorial.&nbsp;Ăn trưa tại nh&agrave; h&agrave;ng địa phương. Sau bữa trưa, đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng, nghỉ ngơi.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/Tsim-Sha-Tsui-in-Hong-Kong.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p>Buổi chiều, xe đưa đo&agrave;n đi ăn tối bằng m&oacute;n nướng BBQ. Đo&agrave;n tự do tham quan mua sắm tại chợ Q&uacute;y B&agrave; (Ladies Market) &ndash; dọc con đường hơn 1 km, b&aacute;n mọi mặt h&agrave;ng từ h&agrave;ng hiệu đến b&igrave;nh d&acirc;n gồm đồ lưu niệm, &aacute;o thun, quần jean, t&uacute;i x&aacute;ch, giầy dep, n&oacute;n, đồ chơi trẻ em,&hellip; với gi&aacute; tốt.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/shopping-hong-kong.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 03: HỒNG K&Ocirc;NG &ndash; DISNEYLAND (M&Ugrave;NG 4 TẾT)</strong></span></p>\r\n\r\n<p>&nbsp;Sau khi ăn s&aacute;ng tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch khởi h&agrave;nh tham quan c&ocirc;ng vi&ecirc;n Disneyland, tự do tham gia c&aacute;c tr&ograve; chơi v&agrave; xem c&aacute;c chương tr&igrave;nh biểu diễn đặc sắc như: Golden Mickey, hoạt cảnh lại c&aacute;c phim từng đoạt giải của Walt Disney, ng&agrave;y hội Vua Sư Tử, Disney diễu h&agrave;nh. Qu&yacute; kh&aacute;ch tự t&uacute;c ăn trưa tại Disneyland.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/disneyland-hong-kong-2.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p>Buổi tối, qu&yacute; kh&aacute;ch c&oacute; thể xem biểu diễn bắn ph&aacute;o hoa. Xe đưa qu&yacute; kh&aacute;ch đi ăn tối, sau đ&oacute; về kh&aacute;ch sạn nghỉ ngơi. Tự do kh&aacute;m ph&aacute; HongKong về đ&ecirc;m.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/ladies-market.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong>NG&Agrave;Y 04: HỒNG K&Ocirc;NG &ndash; TP.HỒ CH&Iacute; MINH (M&Ugrave;NG 5 TẾT)</strong></span></p>\r\n\r\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. L&agrave;m thủ tục trả ph&ograve;ng, đo&agrave;n khởi h&agrave;nh tham quan:&nbsp;Đỉnh n&uacute;i Th&aacute;i B&igrave;nh (Victory Peak) - cao 552m, từ tr&ecirc;n đỉnh c&oacute; thể nh&igrave;n thấy cảng Victoria, v&agrave; khu vực trung t&acirc;m HongKong. Vịnh nước cạn (Repluse Bay) &ndash; trải c&aacute;t d&agrave;i h&igrave;nh lưỡi liềm v&agrave; l&agrave; một trong những b&atilde;i biển đẹp nhật Hồng K&ocirc;ng, một trong những khu d&acirc;n cư sang trọng v&agrave; gi&aacute; nh&agrave; đất đắt đỏ nhất Hongkong.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/vinh-nuoc-can.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p>Tham quan trung t&acirc;m triễn l&atilde;m HongKong (Exhibition and Convention Center) v&agrave; Quảng Trường Sen V&agrave;ng (Golden Bauhinia Square) - nơi diễn ra buổi lễ b&agrave;n giao Hồng K&ocirc;ng về Trung Quốc ng&agrave;y 1/7/1997. Sau đ&oacute; ăn trưa tại nh&agrave; h&agrave;ng địa phương.</p>\r\n\r\n<p>Đo&agrave;n tiếp tục tham quan Thiền Viện Ch&iacute; Li&ecirc;n &ndash; được x&acirc;y dựng độc đ&aacute;o với to&agrave;n bộ c&ocirc;ng tr&igrave;nh bằng gỗ kh&ocirc;ng d&ugrave;ng một c&acirc;y đinh n&agrave;o để x&acirc;u kết c&aacute;c mối nối với nhau theo kiến tr&uacute;c đời Đường, tham quan Vườn Nam Li&ecirc;n mang n&eacute;t kiến tr&uacute;c Nhật Bản nổi bật với một ng&ocirc;i đền h&igrave;nh b&aacute;t gi&aacute;c tọa lạc tr&ecirc;n một hồ nước nhỏ với m&agrave;u v&agrave;ng &oacute;ng &aacute;nh, tại đ&acirc;y Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; thể chi&ecirc;m ngưỡng h&agrave;ng ng&agrave;n c&acirc;y bonsai c&aacute;c loại.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/du-lich-hong-kong-mieu-huynh-dai-tien.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p>Tiếp tục tham quan Miếu Huỳnh Đại Ti&ecirc;n v&agrave; mua sắm tại cửa h&agrave;ng trưng b&agrave;y v&agrave; sản xuất trang sức đ&aacute; qu&yacute; v&agrave; cửa h&agrave;ng miễn thuế PDW Mall.</p>\r\n\r\n<p><a href=\"http://vietnamtourism.net.vn/\"><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/HONG%20KONG%20THAM%20QUYEN/Sogo_Hong_Kong.jpg\" style=\"height:280px; width:670px\" /></a></p>\r\n\r\n<p>Sau đ&oacute; qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng địa phương. Đến giờ hẹn, xe v&agrave; hướng dẫn vi&ecirc;n đưa qu&yacute; kh&aacute;ch ra s&acirc;n bay để đ&aacute;p chuyến bay HX538 (23:05 - 00050) về Việt Nam. Kết th&uacute;c&nbsp;<a href=\"http://vietnamtourism.net.vn/\"><strong>tour du lịch Hồng K&ocirc;ng</strong></a>&nbsp;chia tay qu&yacute; kh&aacute;ch v&agrave; hẹn gặp lại.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR GH&Eacute;P ĐO&Agrave;N:</strong></span></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:670px\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"2\">\r\n			<p style=\"text-align:center\"><strong>GI&Aacute; TOUR</strong></p>\r\n\r\n			<p style=\"text-align:center\"><strong>VNĐ/KH&Aacute;CH</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>NGƯỜI LỚN</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>TRẺ EM</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>TRẺ NHỎ</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>PH&Ograve;NG ĐƠN</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>20.990.000</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>16.990.000</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6,549,000</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>6,000,000</strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><img alt=\"\" src=\"http://vietnamtourism.net.vn/upload/files/2014/SINGAPORE/hotline_vietnamtourism.jpg\" style=\"width:670px\" /></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR BAO GỒM :</strong></span></p>\r\n\r\n<p>- V&eacute; m&aacute;y bay khứ hồi.</p>\r\n\r\n<p>- Visa nhập cảnh Hong Kong theo đo&agrave;n.</p>\r\n\r\n<p>- Kh&aacute;ch sạn ti&ecirc;u chuẩn 4* (02 người/ ph&ograve;ng). Bố tr&iacute; ph&ograve;ng 3 khi cần thiết gồm 1 giường lớn&nbsp; hoặc 2 giường đơn + 1extra bed.</p>\r\n\r\n<p>- Ăn uống, xe đưa đ&oacute;n v&agrave; ph&iacute; tham quan theo chương tr&igrave;nh.</p>\r\n\r\n<p>- HDV nhiệt t&igrave;nh suốt tuyến.</p>\r\n\r\n<p>- Qu&agrave; tặng: n&oacute;n du lịch</p>\r\n\r\n<p>- Bảo hiểm Du Lịch (210.000.000VNĐ/ trường hợp). Kh&ocirc;ng &aacute;p dụng cho kh&aacute;ch từ 85 tuổi trở l&ecirc;n.&nbsp;</p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong>GI&Aacute; TOUR KH&Ocirc;NG BAO GỒM:</strong></span></p>\r\n\r\n<p>- Hộ chiếu c&ograve;n hiệu lực 6 th&aacute;ng t&iacute;nh đến ng&agrave;y về.</p>\r\n\r\n<p>- Ph&iacute; h&agrave;nh l&yacute; qu&aacute; cước v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c.</p>\r\n\r\n<p>- Visa t&aacute;i nhập Việt Nam cho người quốc tịch nước ngo&agrave;i l&agrave; 35usd/kh&aacute;ch/lần. Ri&ecirc;ng những nước được miễn visa Việt Nam như: Anh, Ph&aacute;p, Đức, T&acirc;y Ban Nha, Italia, Nhật Bản, H&agrave;n Quốc, Nauy, Phần Lan, Đan Mạch, Thụy Điển, Nga&hellip; để được miễn visa th&igrave; c&aacute;c lần nhập cảnh v&agrave;o Việt Nam phải c&aacute;ch nhau từ 30 ng&agrave;y trở l&ecirc;n, nếu dưới 30 ng&agrave;y th&igrave; phải l&agrave;m c&ocirc;ng văn t&aacute;i nhập l&agrave; 115usd/kh&aacute;ch/lần. Đối với trường hợp kh&aacute;ch c&oacute; visa Việt Nam nhiều lần, kh&aacute;ch kh&ocirc;ng cần l&agrave;m visa t&aacute;i nhập nhưng lưu &yacute; mỗi lần nhập cảnh kh&ocirc;ng qu&aacute; từ 3 đến 6 th&aacute;ng (t&ugrave;y theo visa)</p>\r\n\r\n<p>- Tiền nước uống, bồi dưỡng cho hướng dẫn địa phương&nbsp; l&agrave; 3usd/ng&agrave;y/kh&aacute;c. Xe đưa đ&oacute;n s&acirc;n bay.</p>\r\n'),
(7, 2, 1, 'DU LỊCH HỒNG KÔNG - QUẢNG CHÂU THẨM QUYẾN TỪ HÀ NỘI', '5 ngày 4 đêm', 'Hà Nội', 2, 'Liên hệ', 14990000, 14990000, 'Từ 10 - 20 triệu', '0', 8, '1521792356HongKong_QuangChau.jpg', 'inner02', '<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"color:#c0392b\"><strong>NHỮNG ĐIỂM ĐẶC SẮC CỦA CHƯƠNG TR&Igrave;NH MANG LẠI CHO DU KH&Aacute;CH</strong></span></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 01: H&Agrave; NỘI &ndash; HỒNG K&Ocirc;NG &ndash; N&Uacute;I TH&Aacute;I B&Igrave;NH &ndash; MIẾU THẦN T&Agrave;I</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Qu&yacute; kh&aacute;ch tập trung tại s&acirc;n bay&nbsp;<strong>Nội B&agrave;i</strong>,&nbsp;đo&agrave;n l&agrave;m thủ tục cho đo&agrave;n đi du lịch&nbsp;<strong>Hồng K&ocirc;ng .&nbsp;</strong>Đến s&acirc;n bay Quốc Tế<strong>&nbsp;Chek Lap Kok</strong>, xe v&agrave; HDV địa phương đ&oacute;n v&agrave; đưa đo&agrave;n đi tham quan&nbsp;<strong>Cầu Thanh M&atilde;</strong><strong>&nbsp;&nbsp;(Tsing Ma Bridge)</strong>&nbsp;được x&acirc;y dựng năm 1997 nối liền hai h&ograve;n đảo quan trọng của Hồng K&ocirc;ng l&agrave; Tsing Yi v&agrave; Ma Wan &ndash; cầu treo đẹp nhất Ch&acirc;u &Aacute; v&agrave; lớn thứ 7 tr&ecirc;n thế giới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp tục qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute;&nbsp;<strong>đỉnh</strong><strong>&nbsp;&lsquo;&rsquo;N&uacute;i Th&aacute;i B&igrave;nh&rsquo;&rsquo;</strong>&nbsp;nơi c&oacute; gi&aacute; đất đắt nhất thế giới<strong>,</strong>&nbsp;ngọn n&uacute;i Th&aacute;i B&igrave;nh cao 552m nằm giữa đảo Hồng K&ocirc;ng v&agrave; Cửu Long. Qu&yacute; kh&aacute;ch c&oacute; thể ngắm nh&igrave;n to&agrave;n cảnh&nbsp;<strong>&lsquo;&rsquo;vịnh Victoria&rsquo;&rsquo;,</strong>&nbsp; Sau đ&oacute; đo&agrave;n xuống n&uacute;i Th&aacute;i B&igrave;nh c&ugrave;ng<strong>&nbsp;&lsquo;&rsquo;xe điện Cổ Điển&rsquo;&rsquo;</strong>&nbsp;để ngắm to&agrave;n cảnh tr&ecirc;n cao từ tr&ecirc;n n&uacute;i.. đo&agrave;n tiếp tục tham quan&nbsp;<strong>&lsquo;&rsquo;Vịnh Nước Cạn&rsquo;&rsquo;.&nbsp;</strong>Gh&eacute;<strong>&nbsp;&lsquo;&rsquo;Miếu Thần T&agrave;i&rsquo;&rsquo;&nbsp;</strong>hướng biển ngắm cảnh đẹp ho&agrave;ng h&ocirc;n.&nbsp;<strong>cầu ph&uacute;c cho gia đ&igrave;nh.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Buổi tối xe đưa đo&agrave;n đi d&ugrave;ng cơm tối với m&oacute;n&nbsp;<strong>&lsquo;&rsquo;BBQ H&agrave;n Quốc&rsquo;&rsquo;</strong>&nbsp;tại khu mua sắm&nbsp;<strong>&lsquo;&rsquo;Chợ Đ&agrave;n B&agrave;&rsquo;&rsquo;,&nbsp;</strong>sau khi d&ugrave;ng cơm tối Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; mua sắm tại khu trung t&acirc;m thương mại&nbsp;<strong>&lsquo;&rsquo;Chợ Đ&agrave;n B&agrave;&rsquo;&rsquo; &lsquo;&rsquo;MongKok&rsquo;&rsquo;..&nbsp;</strong>sầm uất nhất Hồng K&ocirc;ng&hellip; HDV v&agrave; xe đưa đo&agrave;n về Kh&aacute;ch sạn, Nghỉ đ&ecirc;m tại Hồng K&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 02: HỒNG K&Ocirc;NG &ndash; SKY 100 &ndash; CITY TOUR &ndash; QUẢNG CH&Acirc;U</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">D&ugrave;ng điểm t&acirc;m tại kh&aacute;ch sạn. Xe đưa đo&agrave;n tham quan&nbsp;<strong>&lsquo;</strong>tham quan&nbsp;<strong>&ldquo;Quảng Trường Dương Tử Kinh&rdquo;, Mua sắm Trung t&acirc;m Sản Xuất V&agrave;ng Bạc Đ&aacute; Qu&yacute;,</strong>&nbsp;<strong>trung t&acirc;m PDW&hellip;&nbsp;</strong>kh&aacute;m ph&aacute;&nbsp;<strong>&ldquo;Tầng 100 của to&agrave; nh&agrave; Sky 100&rdquo;</strong>&nbsp;ngắm nh&igrave;n th&agrave;nh phố Hồng K&ocirc;ng với g&oacute;c nh&igrave;n 360 độ tr&ecirc;n cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Qu&yacute; kh&aacute;ch d&ugrave;ng bữa trưa, tham quan&nbsp;<strong>&rsquo;Nh&agrave; B&agrave;n Giao&rsquo;&rsquo;</strong>, Chụp h&igrave;nh tại&nbsp;<strong>&lsquo;&rsquo;Đại lộ Ng&ocirc;i sao&rsquo;&rsquo;</strong>&nbsp;c&oacute; chiều d&agrave;i gần nửa km gắn 100 ng&ocirc;i sao in dấu v&acirc;n tay v&agrave; chữ k&yacute; của c&aacute;c nh&acirc;n vật nổi tiếng Hong Kong.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đo&agrave;n d&ugrave;ng cơm trưa, sau đ&oacute; Đo&agrave;n đi viếng thăm&nbsp;<strong>&lsquo;&rsquo;Thiền Viện Ch&iacute; Li&ecirc;n&rsquo;&rsquo;.&nbsp;</strong>Tham quan&nbsp;<strong>&lsquo;&rsquo;Vườn Nam Li&ecirc;n&rsquo;&rsquo;</strong>&nbsp;mang n&eacute;t kiến tr&uacute;c Nhật Bản nổi bật với một ng&ocirc;i đền h&igrave;nh b&aacute;t gi&aacute;c tọa lạc tr&ecirc;n một hồ nước nhỏ với m&agrave;u v&agrave;ng &oacute;ng &aacute;nh, Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi d&ugrave;ng cơm tối sau đ&oacute; đến&nbsp;<strong>Quảng Ch&acirc;u.</strong>&nbsp;Qu&yacute; kh&aacute;ch nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 03: QUẢNG CH&Acirc;U &ndash; THẨM QUYẾN</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">D&ugrave;ng điểm t&acirc;m tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch tham quan :&nbsp;<strong>Tượng Ngũ Dương Th&agrave;nh</strong><strong>,&nbsp;</strong><strong>Nh&agrave; tượng niệm T&ocirc;n Trung Sơn</strong><strong>,&nbsp;</strong><strong>Ho&agrave;ng Hoa Cương của 72 Liệt sĩ</strong><strong>,&nbsp;</strong>Đo&agrave;n d&ugrave;ng cơm trưa, Qu&yacute; kh&aacute;ch tiếp tục tham quan&nbsp;<strong>T</strong><strong>rung t&acirc;m nghi&ecirc;n cứu y dược Trung Hoa</strong><strong>.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau đ&oacute; Qu&yacute; kh&aacute;ch tự do tham quan mua sắm tại<strong>&nbsp;Bắc Kinh Lộ.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kết th&uacute;c chương tr&igrave;nh đo&agrave;n khởi h&agrave;nh đi&nbsp;<strong>Thẩm Quyến.</strong>&nbsp;Nghỉ đ&ecirc;m tại&nbsp;<strong>Thẩm Quyến.</strong></span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 04: KH&Aacute;M PH&Aacute; THẨM QUYẾN</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">D&ugrave;ng điểm t&acirc;m tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>&ldquo;</strong><strong>Viện Bảo T&agrave;ng&nbsp;</strong><strong>Ngọc B&iacute;ch</strong><strong>&rdquo;</strong>. tham quan &ldquo;C&ocirc;ng Vi&ecirc;n Li&ecirc;n Hoa Sơn&rdquo; với &ldquo;Tượng Đặng Tiểu B&igrave;nh&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp tục kh&aacute;m ph&aacute;&nbsp;<strong>&lsquo;&rsquo;</strong><strong>L&agrave;ng văn h&oacute;a d&acirc;n tộc Trung Hoa &ndash; Trung Hoa Cẩm T&uacute;&rsquo;&rsquo;</strong>&nbsp;với c&aacute;c kỳ quan thu nhỏ nổi tiếng của Trung Quốc:&nbsp;<strong>Vạn L&yacute; Trường Th&agrave;nh, Cố Cung, T&acirc;y An B&igrave;nh M&atilde; Dũng&hellip;&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Buổi chiều: Qu&yacute; kh&aacute;ch thưởng &nbsp;thức c&aacute;c show biểu diễn rất hay của c&aacute;c d&acirc;n tộc thiểu số tại đ&acirc;y như: Show &nbsp;<strong>&lsquo;&rsquo;Quyến Rũ Đ&ocirc;ng Phương&rsquo;&rsquo;</strong>&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau đ&oacute;, qu&yacute; kh&aacute;ch tự do mua sắm tại<strong>&nbsp;&lsquo;&rsquo;chợ Đ&ocirc;ng M&ocirc;n&rsquo;&rsquo;</strong>&nbsp;&ndash; khu mua sắm nổi tiếng của Thẩm Quyến.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ăn tối tại nh&agrave; h&agrave;ng địa phương</strong>, sau đ&oacute; xe đưa Qu&yacute; kh&aacute;ch đi về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">NG&Agrave;Y 05: THẨM QUYẾN &ndash; HỒNG K&Ocirc;NG &ndash; H&Agrave; NỘI</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ăn s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch l&agrave;m thủ tục trả ph&ograve;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xe v&agrave; hướng dẫn đưa đo&agrave;n đi tham quan&nbsp;<strong>&ldquo;Si&ecirc;u thị than tre&rdquo;</strong>&nbsp;-&nbsp;<strong>chuy&ecirc;n b&aacute;n c&aacute;c loại vật dụng hằng ng&agrave;y</strong><strong>&nbsp;được l&agrave;m từ than hoạt t&iacute;nh, c&oacute; chức năng khử m&ugrave;i, diệt khuẩn&hellip;..</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">xe v&agrave; HDV đ&oacute;n qu&yacute; kh&aacute;ch đưa ra bến t&agrave;u &ndash;&nbsp;<strong>ngồi t&agrave;u về H</strong><strong>ồ</strong><strong>ng K&ocirc;ng</strong>&nbsp;đ&aacute;p chuyến bay về H&agrave; Nội. Kết th&uacute;c chương tr&igrave;nh du lịch Hồng K&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"color:#c0392b\"><strong><span style=\"font-size:14px\">GI&Aacute; TOUR GH&Eacute;P ĐO&Agrave;N:</span></strong></span></p>\r\n\r\n<table align=\"left\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"2\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong>KHỞI H&Agrave;NH</strong></span></p>\r\n			</td>\r\n			<td colspan=\"3\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong>GI&Aacute; TOUR TRỌN G&Oacute;I CHO 1 KH&Aacute;CH</strong></span><span style=\"font-size:14px\"><strong>(Trẻ em ngủ chung với người lớn đi k&egrave;m)</strong></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><span style=\"font-size:14px\">Người lớn</span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:14px\">Từ 11 tuổi trở l&ecirc;n</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><span style=\"font-size:14px\">Trẻ em</span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:14px\">Từ 02 đến 10 tuổi</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\"><span style=\"font-size:14px\">Trẻ em</span></p>\r\n\r\n			<p style=\"text-align:center\">